import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../../contexts/LanguageContext';
import {
  ChartBarIcon,
  ArrowTrendingUpIcon,
  ArrowTrendingDownIcon,
  LightBulbIcon,
  ClockIcon,
  ExclamationTriangleIcon,
  CheckCircleIcon,
  CurrencyRupeeIcon,
  CalendarIcon,
  TrendingUpIcon
} from '@heroicons/react/24/outline';
import AnimatedPage from './AnimatedPage';

const MarketIntelligencePage = ({ onBack }) => {
  const [timeRange, setTimeRange] = useState('week');
  const [predictions, setPredictions] = useState(null);
  const [marketTrends, setMarketTrends] = useState(null);
  const [recommendations, setRecommendations] = useState([]);
  const [priceHistory, setPriceHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const { language } = useLanguage();

  useEffect(() => {
    loadMarketIntelligence();
  }, [timeRange]);

  const loadMarketIntelligence = () => {
    setLoading(true);
    
    // Mock price prediction data
    const mockPredictions = {
      currentPrice: 8500,
      predictedPrice: 9200,
      confidence: 85,
      trend: 'up',
      timeframe: '7 days',
      factors: [
        { factor: language === 'te' ? 'వాతావరణ పరిస్థితులు' : 'Weather Conditions', impact: 'positive', weight: 0.3 },
        { factor: language === 'te' ? 'ఎగుమతి డిమాండ్' : 'Export Demand', impact: 'positive', weight: 0.25 },
        { factor: language === 'te' ? 'సరఫరా శ్రేణి' : 'Supply Chain', impact: 'neutral', weight: 0.2 },
        { factor: language === 'te' ? 'ప్రభుత్వ విధానాలు' : 'Government Policies', impact: 'positive', weight: 0.15 },
        { factor: language === 'te' ? 'మార్కెట్ సెంటిమెంట్' : 'Market Sentiment', impact: 'positive', weight: 0.1 }
      ]
    };
    setPredictions(mockPredictions);

    // Mock market trends
    const mockTrends = {
      shortTerm: { trend: 'up', strength: 'strong', duration: '3-5 days' },
      mediumTerm: { trend: 'up', strength: 'moderate', duration: '2-3 weeks' },
      longTerm: { trend: 'stable', strength: 'weak', duration: '1-2 months' },
      volatility: 'medium',
      supportLevel: 7800,
      resistanceLevel: 9500
    };
    setMarketTrends(mockTrends);

    // Mock recommendations
    const mockRecommendations = [
      {
        type: 'sell',
        priority: 'high',
        title: language === 'te' ? 'అమ్మకం సిఫార్సు' : 'Sell Recommendation',
        description: language === 'te' ? 'ఈ వారం ధరలు పెరుగుతున్నాయి. మీ టర్మరిక్‌ను వేగంగా అమ్మండి.' : 'Prices are rising this week. Sell your turmeric quickly.',
        expectedPrice: 9200,
        timeframe: language === 'te' ? '3-5 రోజులు' : '3-5 days',
        confidence: 85
      },
      {
        type: 'hold',
        priority: 'medium',
        title: language === 'te' ? 'ఉంచండి' : 'Hold Recommendation',
        description: language === 'te' ? 'ధరలు స్థిరంగా ఉన్నాయి. మరిన్ని సమాచారం కోసం వేచి ఉండండి.' : 'Prices are stable. Wait for more information.',
        expectedPrice: 8500,
        timeframe: language === 'te' ? '1-2 వారాలు' : '1-2 weeks',
        confidence: 70
      },
      {
        type: 'buy',
        priority: 'low',
        title: language === 'te' ? 'కొనుగోలు సిఫార్సు' : 'Buy Recommendation',
        description: language === 'te' ? 'భవిష్యత్తులో ధరలు పెరగవచ్చు. ఇప్పుడు కొనుగోలు చేయండి.' : 'Prices may rise in future. Buy now.',
        expectedPrice: 9000,
        timeframe: language === 'te' ? '2-3 వారాలు' : '2-3 weeks',
        confidence: 60
      }
    ];
    setRecommendations(mockRecommendations);

    // Mock price history
    const mockHistory = [
      { date: '2024-01-01', price: 7200, volume: 1500 },
      { date: '2024-01-08', price: 7500, volume: 1800 },
      { date: '2024-01-15', price: 7800, volume: 2000 },
      { date: '2024-01-22', price: 8200, volume: 2200 },
      { date: '2024-01-29', price: 8500, volume: 2500 },
      { date: '2024-02-05', price: 8800, volume: 2800 },
      { date: '2024-02-12', price: 9200, volume: 3000 }
    ];
    setPriceHistory(mockHistory);

    setLoading(false);
  };

  const getTrendColor = (trend) => {
    switch (trend) {
      case 'up': return 'text-green-600';
      case 'down': return 'text-red-600';
      case 'stable': return 'text-blue-600';
      default: return 'text-gray-600';
    }
  };

  const getTrendIcon = (trend) => {
    switch (trend) {
      case 'up': return ArrowTrendingUpIcon;
      case 'down': return ArrowTrendingDownIcon;
      case 'stable': return TrendingUpIcon;
      default: return ChartBarIcon;
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high': return 'bg-red-100 text-red-800';
      case 'medium': return 'bg-yellow-100 text-yellow-800';
      case 'low': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getTypeColor = (type) => {
    switch (type) {
      case 'sell': return 'bg-red-100 text-red-800';
      case 'buy': return 'bg-green-100 text-green-800';
      case 'hold': return 'bg-blue-100 text-blue-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const formatCurrency = (amount) => {
    return `₹${amount.toLocaleString()}`;
  };

  return (
    <AnimatedPage 
      title={language === 'te' ? 'మార్కెట్ ఇంటెలిజెన్స్' : 'Market Intelligence'} 
      onBack={onBack} 
      icon={ChartBarIcon}
    >
      {/* Time Range Selector */}
      <div className="flex justify-center mb-8">
        <div className="bg-white rounded-xl p-1 shadow-sm">
          {[
            { id: 'week', label: language === 'te' ? 'వారం' : 'Week' },
            { id: 'month', label: language === 'te' ? 'నెల' : 'Month' },
            { id: 'quarter', label: language === 'te' ? 'త్రైమాసికం' : 'Quarter' }
          ].map((range) => (
            <button
              key={range.id}
              onClick={() => setTimeRange(range.id)}
              className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                timeRange === range.id
                  ? 'bg-green-600 text-white'
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              {range.label}
            </button>
          ))}
        </div>
      </div>

      {/* Price Prediction */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="bg-white rounded-2xl shadow-lg p-6 mb-8"
      >
        <h3 className="text-xl font-bold text-gray-900 mb-4">
          {language === 'te' ? 'ధర అంచనా' : 'Price Prediction'}
        </h3>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <div className="flex items-center space-x-4 mb-4">
              <div className="text-center">
                <div className="text-3xl font-bold text-gray-900">
                  {formatCurrency(predictions?.currentPrice)}
                </div>
                <div className="text-sm text-gray-600">
                  {language === 'te' ? 'ప్రస్తుత ధర' : 'Current Price'}
                </div>
              </div>
              
              <div className="text-center">
                <div className="text-3xl font-bold text-green-600">
                  {formatCurrency(predictions?.predictedPrice)}
                </div>
                <div className="text-sm text-gray-600">
                  {language === 'te' ? 'అంచనా ధర' : 'Predicted Price'}
                </div>
              </div>
            </div>
            
            <div className="flex items-center space-x-2 mb-4">
              <div className={`flex items-center space-x-1 ${getTrendColor(predictions?.trend)}`}>
                {React.createElement(getTrendIcon(predictions?.trend), { className: "h-5 w-5" })}
                <span className="font-semibold">
                  {predictions?.trend === 'up' ? (language === 'te' ? 'పెరుగుతోంది' : 'Rising') :
                   predictions?.trend === 'down' ? (language === 'te' ? 'పడిపోతోంది' : 'Falling') :
                   (language === 'te' ? 'స్థిరంగా' : 'Stable')}
                </span>
              </div>
              <span className="text-sm text-gray-600">
                {predictions?.timeframe}
              </span>
            </div>
            
            <div className="bg-gray-100 rounded-lg p-3">
              <div className="flex items-center justify-between mb-2">
                <span className="text-sm font-medium text-gray-700">
                  {language === 'te' ? 'విశ్వసనీయత' : 'Confidence'}
                </span>
                <span className="text-sm font-bold text-gray-900">
                  {predictions?.confidence}%
                </span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-green-600 h-2 rounded-full transition-all duration-500"
                  style={{ width: `${predictions?.confidence}%` }}
                ></div>
              </div>
            </div>
          </div>
          
          <div>
            <h4 className="font-semibold text-gray-900 mb-3">
              {language === 'te' ? 'ధరను ప్రభావితం చేసే కారకాలు' : 'Factors Affecting Price'}
            </h4>
            <div className="space-y-3">
              {predictions?.factors.map((factor, index) => (
                <div key={index} className="flex items-center justify-between">
                  <span className="text-sm text-gray-700">{factor.factor}</span>
                  <div className="flex items-center space-x-2">
                    <div className={`w-2 h-2 rounded-full ${
                      factor.impact === 'positive' ? 'bg-green-500' :
                      factor.impact === 'negative' ? 'bg-red-500' : 'bg-gray-500'
                    }`}></div>
                    <span className="text-xs text-gray-600">
                      {Math.round(factor.weight * 100)}%
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </motion.div>

      {/* Market Trends */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.1 }}
        className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8"
      >
        <div className="bg-white rounded-2xl shadow-lg p-6">
          <h3 className="text-xl font-bold text-gray-900 mb-4">
            {language === 'te' ? 'మార్కెట్ ట్రెండ్‌లు' : 'Market Trends'}
          </h3>
          
          <div className="space-y-4">
            <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
              <div>
                <h4 className="font-semibold text-gray-900">
                  {language === 'te' ? 'స్వల్పకాలిక' : 'Short Term'}
                </h4>
                <p className="text-sm text-gray-600">{marketTrends?.shortTerm.duration}</p>
              </div>
              <div className="text-right">
                <div className={`font-semibold ${getTrendColor(marketTrends?.shortTerm.trend)}`}>
                  {marketTrends?.shortTerm.trend === 'up' ? '↗' : marketTrends?.shortTerm.trend === 'down' ? '↘' : '→'}
                </div>
                <div className="text-xs text-gray-600">{marketTrends?.shortTerm.strength}</div>
              </div>
            </div>
            
            <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
              <div>
                <h4 className="font-semibold text-gray-900">
                  {language === 'te' ? 'మధ్యకాలిక' : 'Medium Term'}
                </h4>
                <p className="text-sm text-gray-600">{marketTrends?.mediumTerm.duration}</p>
              </div>
              <div className="text-right">
                <div className={`font-semibold ${getTrendColor(marketTrends?.mediumTerm.trend)}`}>
                  {marketTrends?.mediumTerm.trend === 'up' ? '↗' : marketTrends?.mediumTerm.trend === 'down' ? '↘' : '→'}
                </div>
                <div className="text-xs text-gray-600">{marketTrends?.mediumTerm.strength}</div>
              </div>
            </div>
            
            <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
              <div>
                <h4 className="font-semibold text-gray-900">
                  {language === 'te' ? 'దీర్ఘకాలిక' : 'Long Term'}
                </h4>
                <p className="text-sm text-gray-600">{marketTrends?.longTerm.duration}</p>
              </div>
              <div className="text-right">
                <div className={`font-semibold ${getTrendColor(marketTrends?.longTerm.trend)}`}>
                  {marketTrends?.longTerm.trend === 'up' ? '↗' : marketTrends?.longTerm.trend === 'down' ? '↘' : '→'}
                </div>
                <div className="text-xs text-gray-600">{marketTrends?.longTerm.strength}</div>
              </div>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-2xl shadow-lg p-6">
          <h3 className="text-xl font-bold text-gray-900 mb-4">
            {language === 'te' ? 'మార్కెట్ స్థాయిలు' : 'Market Levels'}
          </h3>
          
          <div className="space-y-4">
            <div className="flex items-center justify-between p-3 bg-green-50 rounded-lg">
              <div>
                <h4 className="font-semibold text-green-800">
                  {language === 'te' ? 'సపోర్ట్ స్థాయి' : 'Support Level'}
                </h4>
                <p className="text-sm text-green-600">
                  {language === 'te' ? 'కనీస ధర' : 'Minimum Price'}
                </p>
              </div>
              <div className="text-2xl font-bold text-green-600">
                {formatCurrency(marketTrends?.supportLevel)}
              </div>
            </div>
            
            <div className="flex items-center justify-between p-3 bg-red-50 rounded-lg">
              <div>
                <h4 className="font-semibold text-red-800">
                  {language === 'te' ? 'రెసిస్టెన్స్ స్థాయి' : 'Resistance Level'}
                </h4>
                <p className="text-sm text-red-600">
                  {language === 'te' ? 'గరిష్ట ధర' : 'Maximum Price'}
                </p>
              </div>
              <div className="text-2xl font-bold text-red-600">
                {formatCurrency(marketTrends?.resistanceLevel)}
              </div>
            </div>
            
            <div className="flex items-center justify-between p-3 bg-blue-50 rounded-lg">
              <div>
                <h4 className="font-semibold text-blue-800">
                  {language === 'te' ? 'వోలాటిలిటీ' : 'Volatility'}
                </h4>
                <p className="text-sm text-blue-600">
                  {language === 'te' ? 'ధర మార్పు స్థాయి' : 'Price Change Level'}
                </p>
              </div>
              <div className="text-2xl font-bold text-blue-600 capitalize">
                {marketTrends?.volatility}
              </div>
            </div>
          </div>
        </div>
      </motion.div>

      {/* Recommendations */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.2 }}
        className="bg-white rounded-2xl shadow-lg p-6 mb-8"
      >
        <h3 className="text-xl font-bold text-gray-900 mb-4">
          {language === 'te' ? 'AI సిఫార్సులు' : 'AI Recommendations'}
        </h3>
        
        <div className="space-y-4">
          {recommendations.map((rec, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              className="border border-gray-200 rounded-lg p-4"
            >
              <div className="flex items-start justify-between mb-3">
                <div className="flex items-center space-x-3">
                  <span className={`px-3 py-1 rounded-full text-sm font-medium ${getTypeColor(rec.type)}`}>
                    {rec.type.toUpperCase()}
                  </span>
                  <span className={`px-3 py-1 rounded-full text-sm font-medium ${getPriorityColor(rec.priority)}`}>
                    {rec.priority.toUpperCase()}
                  </span>
                </div>
                <div className="text-right">
                  <div className="text-lg font-bold text-gray-900">
                    {formatCurrency(rec.expectedPrice)}
                  </div>
                  <div className="text-sm text-gray-600">{rec.confidence}% {language === 'te' ? 'విశ్వసనీయత' : 'confidence'}</div>
                </div>
              </div>
              
              <h4 className="font-semibold text-gray-900 mb-2">{rec.title}</h4>
              <p className="text-gray-600 mb-3">{rec.description}</p>
              
              <div className="flex items-center space-x-4 text-sm text-gray-500">
                <div className="flex items-center space-x-1">
                  <ClockIcon className="h-4 w-4" />
                  <span>{rec.timeframe}</span>
                </div>
                <div className="flex items-center space-x-1">
                  <CurrencyRupeeIcon className="h-4 w-4" />
                  <span>{formatCurrency(rec.expectedPrice)}</span>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </motion.div>

      {/* Price History Chart */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.3 }}
        className="bg-white rounded-2xl shadow-lg p-6"
      >
        <h3 className="text-xl font-bold text-gray-900 mb-4">
          {language === 'te' ? 'ధర చరిత్ర' : 'Price History'}
        </h3>
        
        <div className="h-64 flex items-end justify-between space-x-2">
          {priceHistory.map((point, index) => {
            const maxPrice = Math.max(...priceHistory.map(p => p.price));
            const height = (point.price / maxPrice) * 200;
            
            return (
              <div key={point.date} className="flex flex-col items-center flex-1">
                <div className="w-full bg-green-200 rounded-t-lg relative group">
                  <div 
                    className="bg-green-600 rounded-t-lg transition-all duration-500"
                    style={{ height: `${height}px` }}
                  ></div>
                  <div className="absolute -top-8 left-1/2 transform -translate-x-1/2 opacity-0 group-hover:opacity-100 transition-opacity bg-gray-800 text-white text-xs px-2 py-1 rounded">
                    {formatCurrency(point.price)}
                  </div>
                </div>
                <div className="text-xs text-gray-600 mt-2">
                  {new Date(point.date).toLocaleDateString()}
                </div>
                <div className="text-xs text-gray-500">
                  {point.volume} {language === 'te' ? 'వాల్యూమ్' : 'volume'}
                </div>
              </div>
            );
          })}
        </div>
      </motion.div>
    </AnimatedPage>
  );
};

export default MarketIntelligencePage;
