import React, { useState } from 'react';
import { AnimatePresence } from 'framer-motion';
import { LanguageProvider, useLanguage } from '../../contexts/LanguageContext';
import SimpleRuralHomePage from '../Layout/SimpleRuralHomePage';
import MarketPricesPage from '../Pages/MarketPricesPage';
import MySalesPage from '../Pages/MySalesPage';
import LearningPage from '../Pages/LearningPage';
import WeatherPage from '../Pages/WeatherPage';
import MSPPage from '../Pages/MSPPage';
import NotificationsPage from '../Pages/NotificationsPage';
import ProfilePage from '../Pages/ProfilePage';
import LoginForm from '../Auth/LoginForm';
// New advanced features
import BlockchainTraceabilityPage from '../Pages/BlockchainTraceabilityPage';
import PaymentSystemPage from '../Pages/PaymentSystemPage';
import CooperativePage from '../Pages/CooperativePage';
import ImpactAnalyticsPage from '../Pages/ImpactAnalyticsPage';
import AudioSupportPage from '../Pages/AudioSupportPage';
import MarketIntelligencePage from '../Pages/MarketIntelligencePage';
import SmartContractsPage from '../Pages/SmartContractsPage';
import QualityCertificationPage from '../Pages/QualityCertificationPage';

const AppContent = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [user, setUser] = useState(null);
  const { language } = useLanguage();

  const handleNavigate = (page) => {
    setCurrentPage(page);
  };

  const handleBack = () => {
    setCurrentPage('home');
  };

  const handleLogin = (userData) => {
    setUser(userData);
    setCurrentPage('home');
  };

  const handleLogout = () => {
    setUser(null);
    setCurrentPage('home');
  };

  const handleUpdateProfile = (updatedProfile) => {
    setUser(updatedProfile);
  };

  if (!user) {
    return <LoginForm onLogin={handleLogin} />;
  }

  return (
    <div className="min-h-screen">
      <AnimatePresence mode="wait">
        {currentPage === 'home' && (
          <SimpleRuralHomePage key="home" onNavigate={handleNavigate} />
        )}
        {currentPage === 'dashboard' && (
          <MarketPricesPage key="dashboard" onBack={handleBack} />
        )}
        {currentPage === 'ledger' && (
          <MySalesPage key="ledger" onBack={handleBack} />
        )}
        {currentPage === 'learning' && (
          <LearningPage key="learning" onBack={handleBack} />
        )}
        {currentPage === 'weather' && (
          <WeatherPage key="weather" onBack={handleBack} />
        )}
        {currentPage === 'msp' && (
          <MSPPage key="msp" onBack={handleBack} />
        )}
        {currentPage === 'notifications' && (
          <NotificationsPage key="notifications" onBack={handleBack} />
        )}
        {currentPage === 'profile' && (
          <ProfilePage key="profile" onBack={handleBack} user={user} onLogout={handleLogout} onUpdateProfile={handleUpdateProfile} />
        )}
        {/* New advanced features */}
        {currentPage === 'blockchain' && (
          <BlockchainTraceabilityPage key="blockchain" onBack={handleBack} />
        )}
        {currentPage === 'payment' && (
          <PaymentSystemPage key="payment" onBack={handleBack} />
        )}
        {currentPage === 'cooperative' && (
          <CooperativePage key="cooperative" onBack={handleBack} />
        )}
        {currentPage === 'analytics' && (
          <ImpactAnalyticsPage key="analytics" onBack={handleBack} />
        )}
        {currentPage === 'audio' && (
          <AudioSupportPage key="audio" onBack={handleBack} />
        )}
        {currentPage === 'intelligence' && (
          <MarketIntelligencePage key="intelligence" onBack={handleBack} />
        )}
        {currentPage === 'contracts' && (
          <SmartContractsPage key="contracts" onBack={handleBack} />
        )}
        {currentPage === 'quality' && (
          <QualityCertificationPage key="quality" onBack={handleBack} />
        )}
      </AnimatePresence>

      {/* User Info & Logout */}
      <div className="fixed top-4 right-4 z-40">
        <div className="bg-white rounded-lg shadow-lg p-3 flex items-center space-x-3">
          <div className="w-8 h-8 bg-primary-100 rounded-full flex items-center justify-center">
            <span className="text-primary-600 font-semibold text-sm">
              {user?.name?.charAt(0) || 'F'}
            </span>
          </div>
          <div>
            <div className="text-sm font-medium text-gray-900">{user?.name}</div>
            <div className="text-xs text-gray-500">{user?.phoneNumber}</div>
          </div>
          <button
            onClick={handleLogout}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h7a3 3 0 013 3v1" />
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <LanguageProvider>
      <AppContent />
    </LanguageProvider>
  );
};

export default App;
