import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { 
  CurrencyRupeeIcon, 
  BookOpenIcon, 
  AcademicCapIcon, 
  BellIcon,
  SunIcon,
  ShieldCheckIcon,
  UserIcon,
  Bars3Icon,
  ChevronDownIcon,
  Cog6ToothIcon,
  ArrowRightOnRectangleIcon
} from '@heroicons/react/24/outline';

const SimpleRuralHomePage = ({ onNavigate }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const [language, setLanguage] = useState('en'); // 'en' or 'te'

  const features = [
    {
      id: 'dashboard',
      title: language === 'te' ? 'à°®à°¾à°°à±à°•à±†à°Ÿà± à°§à°°à°²à±' : 'Market Prices',
      subtitle: language === 'te' ? 'à°²à±ˆà°µà± à°Ÿà°°à±à°®à°°à°¿à°•à± à°§à°°à°²à±' : 'Live turmeric prices',
      icon: CurrencyRupeeIcon,
      color: 'bg-green-500',
      description: language === 'te' ? 'à°ªà±à°°à°¸à±à°¤à±à°¤ à°®à°¾à°°à±à°•à±†à°Ÿà± à°§à°°à°²à°¨à± à°¤à°¨à°¿à°–à±€ à°šà±‡à°¯à°‚à°¡à°¿' : 'Check current market prices'
    },
    {
      id: 'ledger',
      title: language === 'te' ? 'à°¨à°¾ à°…à°®à±à°®à°•à°¾à°²à±' : 'My Sales',
      subtitle: language === 'te' ? 'à°¡à°¿à°œà°¿à°Ÿà°²à± à°²à±†à°¡à±à°œà°°à±' : 'Digital ledger',
      icon: BookOpenIcon,
      color: 'bg-blue-500',
      description: language === 'te' ? 'à°®à±€ à°…à°®à±à°®à°•à°¾à°²à°¨à± à°°à°¿à°•à°¾à°°à±à°¡à± à°šà±‡à°¸à°¿ à°Ÿà±à°°à°¾à°•à± à°šà±‡à°¯à°‚à°¡à°¿' : 'Record and track your sales'
    },
    {
      id: 'learning',
      title: language === 'te' ? 'à°¨à±‡à°°à±à°šà±à°•à±‹à°‚à°¡à°¿' : 'Learn',
      subtitle: language === 'te' ? 'à°µà±à°¯à°µà°¸à°¾à°¯ à°šà°¿à°Ÿà±à°•à°¾à°²à±' : 'Farming tips',
      icon: AcademicCapIcon,
      color: 'bg-purple-500',
      description: language === 'te' ? 'à°µà±€à°¡à°¿à°¯à±‹à°²à± à°šà±‚à°¸à°¿ à°µà±à°¯à°µà°¸à°¾à°¯à°‚ à°¨à±‡à°°à±à°šà±à°•à±‹à°‚à°¡à°¿' : 'Watch videos and learn farming'
    },
    {
      id: 'notifications',
      title: language === 'te' ? 'à°…à°²à°°à±à°Ÿà±à°²à±' : 'Alerts',
      subtitle: language === 'te' ? 'à°§à°° à°¨à°µà±€à°•à°°à°£à°²à±' : 'Price updates',
      icon: BellIcon,
      color: 'bg-orange-500',
      description: language === 'te' ? 'à°§à°° à°®à°°à°¿à°¯à± à°µà°¾à°¤à°¾à°µà°°à°£ à°…à°²à°°à±à°Ÿà±à°²à°¨à± à°ªà±Šà°‚à°¦à°‚à°¡à°¿' : 'Get price and weather alerts'
    },
    {
      id: 'weather',
      title: language === 'te' ? 'à°µà°¾à°¤à°¾à°µà°°à°£à°‚' : 'Weather',
      subtitle: language === 'te' ? 'à°µà±‡à°¸à°µà°¿' : 'Forecast',
      icon: SunIcon,
      color: 'bg-yellow-500',
      description: language === 'te' ? 'à°µà±à°¯à°µà°¸à°¾à°¯à°‚ à°•à±‹à°¸à°‚ à°µà°¾à°¤à°¾à°µà°°à°£à°¾à°¨à±à°¨à°¿ à°¤à°¨à°¿à°–à±€ à°šà±‡à°¯à°‚à°¡à°¿' : 'Check weather for farming'
    },
    {
      id: 'msp',
      title: language === 'te' ? 'MSP à°°à±‡à°Ÿà±à°²à±' : 'MSP Rates',
      subtitle: language === 'te' ? 'à°ªà±à°°à°­à±à°¤à±à°µ à°§à°°à°²à±' : 'Government prices',
      icon: ShieldCheckIcon,
      color: 'bg-indigo-500',
      description: language === 'te' ? 'à°•à°¨à±€à°¸ à°®à°¦à±à°¦à°¤à± à°§à°°' : 'Minimum Support Price'
    }
  ];

  const handleFeatureClick = (featureId) => {
    onNavigate(featureId);
  };

  const handleLanguageToggle = () => {
    setLanguage(language === 'en' ? 'te' : 'en');
  };

  const handleNavClick = (section) => {
    if (section === 'dashboard') {
      onNavigate('dashboard');
    } else if (section === 'ledger') {
      onNavigate('ledger');
    } else if (section === 'learning') {
      onNavigate('learning');
    } else if (section === 'contact') {
      // Handle contact action
      alert(language === 'te' ? 'à°¸à°‚à°ªà±à°°à°¦à°¿à°‚à°šà°‚à°¡à°¿: +91 9876543210' : 'Contact: +91 9876543210');
    }
  };

  const handleLogout = () => {
    setIsProfileOpen(false);
    // Handle logout logic here
    alert(language === 'te' ? 'à°²à°¾à°—à± à°…à°µà±à°Ÿà± à°šà±‡à°¯à°¬à°¡à°¿à°‚à°¦à°¿' : 'Logged out successfully');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-yellow-50 via-orange-50 to-red-50 relative overflow-hidden">
      {/* Turmeric background images */}
      <div className="absolute inset-0 opacity-5">
        {/* Turmeric field background */}
        <div className="absolute inset-0 w-full h-full bg-gradient-to-br from-green-200 via-yellow-200 to-orange-200"></div>
      </div>
      
      {/* Turmeric images overlay */}
      <div className="absolute inset-0 opacity-15">
        {/* Turmeric root patterns */}
        <div className="absolute top-20 left-10 w-32 h-32 bg-gradient-to-br from-yellow-300 to-orange-400 rounded-full shadow-lg"></div>
        <div className="absolute top-40 right-20 w-24 h-24 bg-gradient-to-br from-orange-300 to-red-400 rounded-full shadow-lg"></div>
        <div className="absolute bottom-32 left-32 w-28 h-28 bg-gradient-to-br from-yellow-400 to-orange-500 rounded-full shadow-lg"></div>
        <div className="absolute bottom-20 right-10 w-20 h-20 bg-gradient-to-br from-orange-400 to-red-500 rounded-full shadow-lg"></div>
        
        {/* Turmeric leaf patterns */}
        <div className="absolute top-60 left-1/4 w-16 h-16 bg-gradient-to-br from-green-300 to-green-500 rounded-full shadow-lg"></div>
        <div className="absolute top-80 right-1/3 w-12 h-12 bg-gradient-to-br from-green-400 to-green-600 rounded-full shadow-lg"></div>
        <div className="absolute bottom-60 left-1/3 w-14 h-14 bg-gradient-to-br from-green-300 to-green-500 rounded-full shadow-lg"></div>
      </div>
      
      {/* Simple Header */}
      <header className="bg-white/95 backdrop-blur-sm shadow-md border-b-2 border-yellow-200">
        <div className="max-w-6xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            {/* Logo */}
            <div className="flex items-center space-x-3">
              <div className="w-12 h-12 bg-yellow-500 rounded-lg flex items-center justify-center">
                <span className="text-white text-xl">ðŸŒ±</span>
              </div>
              <span className="text-2xl font-bold text-gray-900">TurmerLink</span>
            </div>

            {/* Navigation */}
            <nav className="hidden md:flex items-center space-x-6">
              <button 
                onClick={() => handleNavClick('dashboard')}
                className="text-primary-600 font-semibold hover:text-primary-700 transition-colors"
              >
                {language === 'te' ? 'à°¹à±‹à°®à±' : 'Home'}
              </button>
              <button 
                onClick={() => handleNavClick('dashboard')}
                className="text-gray-600 hover:text-primary-600 transition-colors"
              >
                {language === 'te' ? 'à°®à°¾à°°à±à°•à±†à°Ÿà± à°§à°°à°²à±' : 'Market Prices'}
              </button>
              <button 
                onClick={() => handleNavClick('ledger')}
                className="text-gray-600 hover:text-primary-600 transition-colors"
              >
                {language === 'te' ? 'à°¨à°¾ à°…à°®à±à°®à°•à°¾à°²à±' : 'My Sales'}
              </button>
              <button 
                onClick={() => handleNavClick('learning')}
                className="text-gray-600 hover:text-primary-600 transition-colors"
              >
                {language === 'te' ? 'à°¨à±‡à°°à±à°šà±à°•à±‹à°‚à°¡à°¿' : 'Learning'}
              </button>
              <button 
                onClick={() => handleNavClick('contact')}
                className="text-gray-600 hover:text-primary-600 transition-colors"
              >
                {language === 'te' ? 'à°¸à°‚à°ªà±à°°à°¦à°¿à°‚à°šà°‚à°¡à°¿' : 'Contact'}
              </button>
            </nav>

            {/* Language Toggle & Profile */}
            <div className="hidden md:flex items-center space-x-4">
              <button 
                onClick={handleLanguageToggle}
                className="px-3 py-2 bg-gray-100 rounded-lg text-gray-700 hover:bg-gray-200 transition-colors font-semibold"
              >
                {language === 'te' ? 'English' : 'à°¤à±†à°²à±à°—à±'}
              </button>
              
              {/* User Profile Dropdown */}
              <div className="relative">
                <button 
                  onClick={() => setIsProfileOpen(!isProfileOpen)}
                  className="flex items-center space-x-2 bg-primary-600 text-white px-4 py-2 rounded-lg hover:bg-primary-700 transition-colors"
                >
                  <UserIcon className="h-4 w-4" />
                  <span>{language === 'te' ? 'à°°à°¾à°® à°°à°¾à°µà±' : 'Rama Rao'}</span>
                  <ChevronDownIcon className="h-4 w-4" />
                </button>
                
                {/* Profile Dropdown */}
                {isProfileOpen && (
                  <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 z-50">
                    <div className="p-4 border-b border-gray-200">
                      <div className="flex items-center space-x-3">
                        <div className="w-10 h-10 bg-gradient-to-r from-yellow-400 to-orange-500 rounded-full flex items-center justify-center">
                          <span className="text-white font-bold">R</span>
                        </div>
                        <div>
                          <p className="font-semibold text-gray-900">{language === 'te' ? 'à°°à°¾à°® à°°à°¾à°µà±' : 'Rama Rao'}</p>
                          <p className="text-sm text-gray-600">+91 9876543210</p>
                        </div>
                      </div>
                    </div>
                    <div className="py-2">
                      <button className="w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100 flex items-center space-x-2">
                        <Cog6ToothIcon className="h-4 w-4" />
                        <span>{language === 'te' ? 'à°¸à±†à°Ÿà±à°Ÿà°¿à°‚à°—à±à°²à±' : 'Settings'}</span>
                      </button>
                      <button 
                        onClick={handleLogout}
                        className="w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100 flex items-center space-x-2"
                      >
                        <ArrowRightOnRectangleIcon className="h-4 w-4" />
                        <span>{language === 'te' ? 'à°²à°¾à°—à± à°…à°µà±à°Ÿà±' : 'Logout'}</span>
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Mobile Menu Button */}
            <button 
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="md:hidden p-2 text-gray-600 hover:text-primary-600"
            >
              <Bars3Icon className="h-6 w-6" />
            </button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="max-w-6xl mx-auto px-6 py-12 relative z-10">
        {/* Welcome Section */}
        <div className="text-center mb-16">
          <div className="flex justify-center mb-6">
            <div className="w-24 h-24 bg-gradient-to-br from-yellow-400 to-orange-500 rounded-full shadow-lg border-4 border-yellow-200 flex items-center justify-center">
              <span className="text-4xl">ðŸŒ±</span>
            </div>
          </div>
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            {language === 'te' ? 'TurmerLink à°•à± à°¸à±à°µà°¾à°—à°¤à°‚' : 'Welcome to TurmerLink'}
          </h1>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">
            {language === 'te' ? 'à°Ÿà°°à±à°®à°°à°¿à°•à± à°°à±ˆà°¤à±à°²à°•à± à°®à±€ à°ªà±‚à°°à±à°¤à°¿ à°µà±à°¯à°µà°¸à°¾à°¯ à°¸à°¹à°šà°°à±à°¡à±' : 'Your complete farming companion for turmeric farmers'}
          </p>
        </div>

        {/* Today's Market Overview */}
        <div className="bg-white/95 backdrop-blur-sm rounded-2xl shadow-lg p-8 mb-12 border border-yellow-200">
          <h2 className="text-2xl font-bold text-gray-900 mb-6 text-center">
            {language === 'te' ? 'à°ˆà°°à±‹à°œà± à°®à°¾à°°à±à°•à±†à°Ÿà± à°…à°µà°²à±‹à°•à°¨à°‚' : 'Today\'s Market Overview'}
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            <div className="text-center">
              <div className="w-16 h-16 bg-gradient-to-br from-green-400 to-emerald-500 rounded-full mx-auto mb-3 shadow-md flex items-center justify-center">
                <span className="text-2xl">ðŸ’°</span>
              </div>
              <div className="text-3xl font-bold text-green-600 mb-2">â‚¹8,500</div>
              <div className="text-gray-600">{language === 'te' ? 'à°¨à°¿à°œà°¾à°®à°¾à°¬à°¾à°¦à± à°§à°°' : 'Nizamabad Price'}</div>
            </div>
            <div className="text-center">
              <div className="w-16 h-16 bg-gradient-to-br from-blue-400 to-cyan-500 rounded-full mx-auto mb-3 shadow-md flex items-center justify-center">
                <span className="text-2xl">ðŸ›¡ï¸</span>
              </div>
              <div className="text-3xl font-bold text-blue-600 mb-2">â‚¹7,500</div>
              <div className="text-gray-600">{language === 'te' ? 'MSP à°°à±‡à°Ÿà±' : 'MSP Rate'}</div>
            </div>
            <div className="text-center">
              <div className="w-16 h-16 bg-gradient-to-br from-orange-400 to-red-500 rounded-full mx-auto mb-3 shadow-md flex items-center justify-center">
                <span className="text-2xl">â˜€ï¸</span>
              </div>
              <div className="text-3xl font-bold text-orange-600 mb-2">28Â°C</div>
              <div className="text-gray-600">{language === 'te' ? 'à°‰à°·à±à°£à±‹à°—à±à°°à°¤' : 'Temperature'}</div>
            </div>
            <div className="text-center">
              <div className="w-16 h-16 bg-gradient-to-br from-purple-400 to-violet-500 rounded-full mx-auto mb-3 shadow-md flex items-center justify-center">
                <span className="text-2xl">ðŸ“ˆ</span>
              </div>
              <div className="text-3xl font-bold text-purple-600 mb-2">+â‚¹200</div>
              <div className="text-gray-600">{language === 'te' ? 'à°§à°° à°®à°¾à°°à±à°ªà±' : 'Price Change'}</div>
            </div>
          </div>
        </div>

        {/* Features Grid */}
        <div className="mb-12">
          <h2 className="text-3xl font-bold text-gray-900 mb-8 text-center">
            {language === 'te' ? 'ðŸŒ± à°®à°¾ à°²à°•à±à°·à°£à°¾à°²à±' : 'Our Features'}
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {features.map((feature) => {
              const Icon = feature.icon;
              return (
                <div
                  key={feature.id}
                  onClick={() => handleFeatureClick(feature.id)}
                  className="bg-white/95 backdrop-blur-sm rounded-2xl shadow-lg p-8 cursor-pointer hover:shadow-xl transition-shadow border-2 border-transparent hover:border-yellow-300"
                >
                  <div className="text-center">
                    <div className={`w-20 h-20 ${feature.color} rounded-2xl flex items-center justify-center mx-auto mb-6`}>
                      <Icon className="h-10 w-10 text-white" />
                    </div>
                    <h3 className="text-2xl font-bold text-gray-900 mb-2">
                      {feature.title}
                    </h3>
                    <p className="text-lg text-gray-600 mb-3">
                      {feature.subtitle}
                    </p>
                    <p className="text-gray-500">
                      {feature.description}
                    </p>
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* Quick Actions */}
        <div className="bg-white/95 backdrop-blur-sm rounded-2xl shadow-lg p-8 border border-yellow-200">
          <h2 className="text-2xl font-bold text-gray-900 mb-6 text-center">
            {language === 'te' ? 'à°¤à±à°µà°°à°¿à°¤ à°šà°°à±à°¯à°²à±' : 'Quick Actions'}
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <button 
              onClick={() => handleFeatureClick('dashboard')}
              className="bg-gradient-to-r from-yellow-500 to-orange-500 text-white py-4 px-8 rounded-xl font-semibold text-lg hover:from-yellow-600 hover:to-orange-600 transition-all shadow-lg"
            >
              {language === 'te' ? 'à°®à°¾à°°à±à°•à±†à°Ÿà± à°§à°°à°²à°¨à± à°¤à°¨à°¿à°–à±€ à°šà±‡à°¯à°‚à°¡à°¿' : 'Check Market Prices'}
            </button>
            <button 
              onClick={() => handleFeatureClick('ledger')}
              className="bg-gradient-to-r from-green-500 to-emerald-500 text-white py-4 px-8 rounded-xl font-semibold text-lg hover:from-green-600 hover:to-emerald-600 transition-all shadow-lg"
            >
              {language === 'te' ? 'à°•à±Šà°¤à±à°¤ à°…à°®à±à°®à°•à°¾à°¨à±à°¨à°¿ à°°à°¿à°•à°¾à°°à±à°¡à± à°šà±‡à°¯à°‚à°¡à°¿' : 'Record New Sale'}
            </button>
          </div>
        </div>
      </div>

      {/* Simple Footer */}
      <footer className="bg-gradient-to-r from-yellow-800 via-orange-800 to-red-800 text-white py-12 relative">
        {/* Footer background pattern */}
        <div className="absolute inset-0 opacity-10">
          <div className="absolute top-4 left-10 w-16 h-16 bg-yellow-300 rounded-full"></div>
          <div className="absolute top-8 right-20 w-12 h-12 bg-orange-300 rounded-full"></div>
          <div className="absolute bottom-8 left-32 w-14 h-14 bg-yellow-400 rounded-full"></div>
          <div className="absolute bottom-4 right-10 w-10 h-10 bg-orange-400 rounded-full"></div>
        </div>
        <div className="max-w-6xl mx-auto px-6 relative z-10">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
            <div>
              <div className="flex items-center space-x-3 mb-4">
                <div className="w-8 h-8 bg-yellow-500 rounded-lg flex items-center justify-center">
                  <span className="text-white text-sm">ðŸŒ±</span>
                </div>
                <span className="text-xl font-bold">TurmerLink</span>
              </div>
              <p className="text-gray-400">
                {language === 'te' ? 'à°Ÿà±†à°•à±à°¨à°¾à°²à°œà±€ à°®à°°à°¿à°¯à± à°ªà°¾à°°à°¦à°°à±à°¶à°•à°¤à°¤à±‹ à°Ÿà°°à±à°®à°°à°¿à°•à± à°°à±ˆà°¤à±à°²à°¨à± à°¶à°•à±à°¤à°¿à°µà°‚à°¤à°‚ à°šà±‡à°¸à±à°¤à±‹à°‚à°¦à°¿.' : 'Empowering turmeric farmers with technology.'}
              </p>
            </div>
            
            <div>
