import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../../contexts/LanguageContext';
import {
  CreditCardIcon,
  ShieldCheckIcon,
  ClockIcon,
  CheckCircleIcon,
  ExclamationTriangleIcon,
  ArrowPathIcon,
  BanknotesIcon,
  DevicePhoneMobileIcon
} from '@heroicons/react/24/outline';
import AnimatedPage from './AnimatedPage';

const PaymentSystemPage = ({ onBack }) => {
  const [paymentMethod, setPaymentMethod] = useState('upi');
  const [amount, setAmount] = useState('');
  const [upiId, setUpiId] = useState('');
  const [loading, setLoading] = useState(false);
  const [paymentStatus, setPaymentStatus] = useState(null);
  const [escrowEnabled, setEscrowEnabled] = useState(true);
  const [transactions, setTransactions] = useState([]);
  const { language } = useLanguage();

  useEffect(() => {
    loadTransactions();
  }, []);

  const loadTransactions = () => {
    // Mock transaction data
    const mockTransactions = [
      {
        id: 'txn_001',
        amount: 50000,
        buyer: 'Rama Rao',
        seller: 'Local Trader',
        status: 'completed',
        method: 'upi',
        date: Date.now() - 86400000,
        batchId: 'batch_001'
      },
      {
        id: 'txn_002',
        amount: 30000,
        buyer: 'Export Company',
        seller: 'Sita Devi',
        status: 'escrow',
        method: 'upi',
        date: Date.now() - 172800000,
        batchId: 'batch_002'
      }
    ];
    setTransactions(mockTransactions);
  };

  const initiatePayment = async () => {
    if (!amount || !upiId) {
      alert(language === 'te' ? 'దయచేసి మొత్తం మరియు UPI ID నమోదు చేయండి' : 'Please enter amount and UPI ID');
      return;
    }

    setLoading(true);
    setPaymentStatus('processing');

    try {
      // Simulate UPI payment process
      await new Promise(resolve => setTimeout(resolve, 3000));
      
      const paymentResult = {
        success: Math.random() > 0.1, // 90% success rate
        transactionId: `txn_${Date.now()}`,
        amount: parseFloat(amount),
        method: paymentMethod,
        timestamp: Date.now()
      };

      if (paymentResult.success) {
        setPaymentStatus('success');
        
        // Add to transactions
        const newTransaction = {
          id: paymentResult.transactionId,
          amount: paymentResult.amount,
          buyer: 'Current User',
          seller: 'Market Buyer',
          status: escrowEnabled ? 'escrow' : 'completed',
          method: paymentMethod,
          date: paymentResult.timestamp,
          batchId: 'batch_new'
        };
        
        setTransactions(prev => [newTransaction, ...prev]);
        
        // Clear form
        setAmount('');
        setUpiId('');
      } else {
        setPaymentStatus('failed');
      }
    } catch (error) {
      console.error('Payment error:', error);
      setPaymentStatus('failed');
    } finally {
      setLoading(false);
    }
  };

  const releaseEscrow = (transactionId) => {
    setTransactions(prev => prev.map(txn => 
      txn.id === transactionId ? { ...txn, status: 'completed' } : txn
    ));
    alert(language === 'te' ? 'ఎస్క్రో విడుదల చేయబడింది' : 'Escrow released successfully');
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed': return 'bg-green-100 text-green-800';
      case 'escrow': return 'bg-yellow-100 text-yellow-800';
      case 'failed': return 'bg-red-100 text-red-800';
      case 'processing': return 'bg-blue-100 text-blue-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusText = (status) => {
    const statusMap = {
      completed: language === 'te' ? 'పూర్తయింది' : 'Completed',
      escrow: language === 'te' ? 'ఎస్క్రోలో' : 'In Escrow',
      failed: language === 'te' ? 'విఫలమైంది' : 'Failed',
      processing: language === 'te' ? 'ప్రాసెస్ చేస్తోంది' : 'Processing'
    };
    return statusMap[status] || status;
  };

  const formatCurrency = (amount) => {
    return `₹${amount.toLocaleString()}`;
  };

  return (
    <AnimatedPage 
      title={language === 'te' ? 'సురక్షిత చెల్లింపు వ్యవస్థ' : 'Secure Payment System'} 
      onBack={onBack} 
      icon={CreditCardIcon}
    >
      {/* Payment Form */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="bg-white rounded-2xl shadow-lg p-6 mb-8"
      >
        <h3 className="text-xl font-bold text-gray-900 mb-4">
          {language === 'te' ? 'చెల్లింపు చేయండి' : 'Make Payment'}
        </h3>
        
        {/* Payment Method Selection */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-3">
            {language === 'te' ? 'చెల్లింపు పద్ధతి' : 'Payment Method'}
          </label>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <button
              onClick={() => setPaymentMethod('upi')}
              className={`p-4 rounded-lg border-2 transition-colors ${
                paymentMethod === 'upi' 
                  ? 'border-green-500 bg-green-50' 
                  : 'border-gray-200 hover:border-gray-300'
              }`}
            >
              <DevicePhoneMobileIcon className="h-8 w-8 mx-auto mb-2 text-green-600" />
              <div className="text-sm font-medium">UPI</div>
            </button>
            
            <button
              onClick={() => setPaymentMethod('bank')}
              className={`p-4 rounded-lg border-2 transition-colors ${
                paymentMethod === 'bank' 
                  ? 'border-blue-500 bg-blue-50' 
                  : 'border-gray-200 hover:border-gray-300'
              }`}
            >
              <BanknotesIcon className="h-8 w-8 mx-auto mb-2 text-blue-600" />
              <div className="text-sm font-medium">{language === 'te' ? 'బ్యాంక్' : 'Bank Transfer'}</div>
            </button>
            
            <button
              onClick={() => setPaymentMethod('card')}
              className={`p-4 rounded-lg border-2 transition-colors ${
                paymentMethod === 'card' 
                  ? 'border-purple-500 bg-purple-50' 
                  : 'border-gray-200 hover:border-gray-300'
              }`}
            >
              <CreditCardIcon className="h-8 w-8 mx-auto mb-2 text-purple-600" />
              <div className="text-sm font-medium">{language === 'te' ? 'కార్డ్' : 'Card'}</div>
            </button>
          </div>
        </div>

        {/* Payment Details */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {language === 'te' ? 'మొత్తం (₹)' : 'Amount (₹)'}
            </label>
            <input
              type="number"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
              placeholder={language === 'te' ? 'మొత్తం నమోదు చేయండి' : 'Enter amount'}
            />
          </div>
          
          {paymentMethod === 'upi' && (
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                UPI ID
              </label>
              <input
                type="text"
                value={upiId}
                onChange={(e) => setUpiId(e.target.value)}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
                placeholder="example@paytm"
              />
            </div>
          )}
        </div>

        {/* Escrow Option */}
        <div className="mb-6">
          <label className="flex items-center space-x-3">
            <input
              type="checkbox"
              checked={escrowEnabled}
              onChange={(e) => setEscrowEnabled(e.target.checked)}
              className="w-5 h-5 text-green-600 border-gray-300 rounded focus:ring-green-500"
            />
            <div>
              <span className="text-sm font-medium text-gray-700">
                {language === 'te' ? 'ఎస్క్రో సేవలను ఉపయోగించండి' : 'Use Escrow Services'}
              </span>
              <p className="text-xs text-gray-500">
                {language === 'te' 
                  ? 'సురక్షిత చెల్లింపు కోసం ఎస్క్రోలో డబ్బును ఉంచండి'
                  : 'Keep money in escrow for secure payment'
                }
              </p>
            </div>
          </label>
        </div>

        {/* Payment Button */}
        <button
          onClick={initiatePayment}
          disabled={loading}
          className="w-full bg-green-600 text-white py-3 px-6 rounded-lg font-semibold hover:bg-green-700 transition-colors disabled:opacity-50"
        >
          {loading ? (
            <div className="flex items-center justify-center space-x-2">
              <ArrowPathIcon className="h-5 w-5 animate-spin" />
              <span>{language === 'te' ? 'చెల్లింపు ప్రాసెస్ చేస్తోంది...' : 'Processing Payment...'}</span>
            </div>
          ) : (
            language === 'te' ? 'చెల్లింపు చేయండి' : 'Make Payment'
          )}
        </button>
      </motion.div>

      {/* Payment Status */}
      {paymentStatus && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="mb-8"
        >
          {paymentStatus === 'success' && (
            <div className="bg-green-50 border border-green-200 rounded-xl p-6 text-center">
              <CheckCircleIcon className="h-12 w-12 text-green-500 mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-green-800 mb-2">
                {language === 'te' ? 'చెల్లింపు విజయవంతంగా పూర్తయింది!' : 'Payment Successful!'}
              </h3>
              <p className="text-green-600">
                {escrowEnabled 
                  ? (language === 'te' ? 'డబ్బు ఎస్క్రోలో ఉంచబడింది' : 'Money held in escrow')
                  : (language === 'te' ? 'చెల్లింపు పూర్తయింది' : 'Payment completed')
                }
              </p>
            </div>
          )}
          
          {paymentStatus === 'failed' && (
            <div className="bg-red-50 border border-red-200 rounded-xl p-6 text-center">
              <ExclamationTriangleIcon className="h-12 w-12 text-red-500 mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-red-800 mb-2">
                {language === 'te' ? 'చెల్లింపు విఫలమైంది' : 'Payment Failed'}
              </h3>
              <p className="text-red-600">
                {language === 'te' ? 'దయచేసి మళ్లీ ప్రయత్నించండి' : 'Please try again'}
              </p>
            </div>
          )}
        </motion.div>
      )}

      {/* Transaction History */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.2 }}
        className="bg-white rounded-2xl shadow-lg p-6"
      >
        <h3 className="text-xl font-bold text-gray-900 mb-4">
          {language === 'te' ? 'లావాదేవీ చరిత్ర' : 'Transaction History'}
        </h3>
        
        <div className="space-y-4">
          {transactions.map((transaction) => (
            <div key={transaction.id} className="border border-gray-200 rounded-lg p-4">
              <div className="flex items-center justify-between mb-2">
                <div>
                  <h4 className="font-semibold text-gray-900">{transaction.id}</h4>
                  <p className="text-sm text-gray-600">
                    {transaction.buyer} → {transaction.seller}
                  </p>
                </div>
                <div className="text-right">
                  <div className="text-lg font-bold text-gray-900">
                    {formatCurrency(transaction.amount)}
                  </div>
                  <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(transaction.status)}`}>
                    {getStatusText(transaction.status)}
                  </span>
                </div>
              </div>
              
              <div className="flex items-center justify-between text-sm text-gray-500">
                <div className="flex items-center space-x-4">
                  <span>{new Date(transaction.date).toLocaleDateString()}</span>
                  <span>{transaction.method.toUpperCase()}</span>
                  <span>{language === 'te' ? 'బ్యాచ్' : 'Batch'}: {transaction.batchId}</span>
                </div>
                
                {transaction.status === 'escrow' && (
                  <button
                    onClick={() => releaseEscrow(transaction.id)}
                    className="px-3 py-1 bg-green-600 text-white rounded-lg text-xs font-medium hover:bg-green-700 transition-colors"
                  >
                    {language === 'te' ? 'ఎస్క్రో విడుదల చేయండి' : 'Release Escrow'}
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      </motion.div>

      {/* Security Features */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.3 }}
        className="mt-8 bg-blue-50 border border-blue-200 rounded-xl p-6"
      >
        <h3 className="text-lg font-semibold text-blue-800 mb-2">
          {language === 'te' ? 'సురక్షిత చెల్లింపు లక్షణాలు' : 'Secure Payment Features'}
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-blue-700">
          <div className="flex items-center space-x-2">
            <ShieldCheckIcon className="h-5 w-5" />
            <span>{language === 'te' ? 'SSL గుప్తీకరణ' : 'SSL Encryption'}</span>
          </div>
          <div className="flex items-center space-x-2">
            <ClockIcon className="h-5 w-5" />
            <span>{language === 'te' ? 'ఎస్క్రో సేవలు' : 'Escrow Services'}</span>
          </div>
          <div className="flex items-center space-x-2">
            <CheckCircleIcon className="h-5 w-5" />
            <span>{language === 'te' ? 'బ్లాక్‌చెయిన్ ధృవీకరణ' : 'Blockchain Verification'}</span>
          </div>
          <div className="flex items-center space-x-2">
            <ExclamationTriangleIcon className="h-5 w-5" />
            <span>{language === 'te' ? 'ఫ్రాడ్ రక్షణ' : 'Fraud Protection'}</span>
          </div>
        </div>
      </motion.div>
    </AnimatedPage>
  );
};

export default PaymentSystemPage;
