import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../../contexts/LanguageContext';
import {
  ChartBarIcon,
  ArrowTrendingUpIcon,
  ArrowTrendingDownIcon,
  CurrencyRupeeIcon,
  CalendarIcon,
  UserIcon,
  ChartPieIcon,
  ClockIcon,
  CheckCircleIcon,
  ExclamationTriangleIcon
} from '@heroicons/react/24/outline';
import AnimatedPage from './AnimatedPage';

const ImpactAnalyticsPage = ({ onBack }) => {
  const [timeRange, setTimeRange] = useState('year');
  const [farmerData, setFarmerData] = useState(null);
  const [marketParticipation, setMarketParticipation] = useState(null);
  const [incomeTrends, setIncomeTrends] = useState([]);
  const [cooperativeParticipation, setCooperativeParticipation] = useState(null);
  const [loading, setLoading] = useState(false);
  const { language } = useLanguage();

  useEffect(() => {
    loadAnalyticsData();
  }, [timeRange]);

  const loadAnalyticsData = () => {
    // Mock farmer income data
    const mockFarmerData = {
      currentIncome: 125000,
      previousIncome: 95000,
      incomeGrowth: 31.6,
      totalSales: 15,
      averagePrice: 8333,
      bestPrice: 9500,
      worstPrice: 7200,
      totalVolume: 15, // quintals
      marketAccess: 85, // percentage
      cooperativeBenefits: 25000
    };
    setFarmerData(mockFarmerData);

    // Mock income trends
    const mockTrends = [
      { month: 'Jan', income: 85000, sales: 8 },
      { month: 'Feb', income: 92000, sales: 9 },
      { month: 'Mar', income: 105000, sales: 10 },
      { month: 'Apr', income: 98000, sales: 9 },
      { month: 'May', income: 112000, sales: 11 },
      { month: 'Jun', income: 125000, sales: 15 }
    ];
    setIncomeTrends(mockTrends);

    // Mock market participation data
    const mockMarketData = {
      totalTransactions: 23,
      successfulTransactions: 21,
      averageTransactionTime: 2.5, // days
      priceImprovement: 15.2, // percentage
      marketReach: ['Local', 'State', 'National', 'Export'],
      participationScore: 87
    };
    setMarketParticipation(mockMarketData);

    // Mock cooperative participation
    const mockCooperativeData = {
      groupOrders: 8,
      totalSavings: 25000,
      collectiveBargaining: 12,
      averageSavings: 3125,
      participationRate: 75
    };
    setCooperativeParticipation(mockCooperativeData);
  };

  const formatCurrency = (amount) => {
    if (amount >= 100000) {
      return `₹${(amount / 100000).toFixed(1)}L`;
    }
    return `₹${amount.toLocaleString()}`;
  };

  const getGrowthColor = (growth) => {
    return growth >= 0 ? 'text-green-600' : 'text-red-600';
  };

  const getGrowthIcon = (growth) => {
    return growth >= 0 ? ArrowTrendingUpIcon : ArrowTrendingDownIcon;
  };

  return (
    <AnimatedPage 
      title={language === 'te' ? 'ఆదాయ ట్రాకింగ్ & విశ్లేషణ' : 'Income Tracking & Analytics'} 
      onBack={onBack} 
      icon={ChartBarIcon}
    >
      {/* Time Range Selector */}
      <div className="flex justify-center mb-8">
        <div className="bg-white rounded-xl p-1 shadow-sm">
          {[
            { id: 'month', label: language === 'te' ? 'నెల' : 'Month' },
            { id: 'quarter', label: language === 'te' ? 'త్రైమాసికం' : 'Quarter' },
            { id: 'year', label: language === 'te' ? 'సంవత్సరం' : 'Year' }
          ].map((range) => (
            <button
              key={range.id}
              onClick={() => setTimeRange(range.id)}
              className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                timeRange === range.id
                  ? 'bg-green-600 text-white'
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              {range.label}
            </button>
          ))}
        </div>
      </div>

      {/* Income Overview */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8"
      >
        <div className="bg-white rounded-2xl shadow-lg p-6 text-center">
          <CurrencyRupeeIcon className="h-12 w-12 text-green-600 mx-auto mb-3" />
          <div className="text-3xl font-bold text-gray-900 mb-2">
            {formatCurrency(farmerData?.currentIncome)}
          </div>
          <div className="text-gray-600 mb-2">
            {language === 'te' ? 'ప్రస్తుత ఆదాయం' : 'Current Income'}
          </div>
          <div className={`text-sm font-medium ${getGrowthColor(farmerData?.incomeGrowth)}`}>
            {farmerData?.incomeGrowth > 0 ? '+' : ''}{farmerData?.incomeGrowth}%
          </div>
        </div>

        <div className="bg-white rounded-2xl shadow-lg p-6 text-center">
          <ChartBarIcon className="h-12 w-12 text-blue-600 mx-auto mb-3" />
          <div className="text-3xl font-bold text-gray-900 mb-2">
            {farmerData?.totalSales}
          </div>
          <div className="text-gray-600 mb-2">
            {language === 'te' ? 'మొత్తం అమ్మకాలు' : 'Total Sales'}
          </div>
          <div className="text-sm text-gray-500">
            {farmerData?.totalVolume} {language === 'te' ? 'క్వింటాళ్లు' : 'quintals'}
          </div>
        </div>

        <div className="bg-white rounded-2xl shadow-lg p-6 text-center">
          <ArrowTrendingUpIcon className="h-12 w-12 text-purple-600 mx-auto mb-3" />
          <div className="text-3xl font-bold text-gray-900 mb-2">
            ₹{farmerData?.averagePrice?.toLocaleString()}
          </div>
          <div className="text-gray-600 mb-2">
            {language === 'te' ? 'సగటు ధర' : 'Average Price'}
          </div>
          <div className="text-sm text-gray-500">
            {language === 'te' ? 'క్వింటల్ కు' : 'per quintal'}
          </div>
        </div>

        <div className="bg-white rounded-2xl shadow-lg p-6 text-center">
          <UserIcon className="h-12 w-12 text-orange-600 mx-auto mb-3" />
          <div className="text-3xl font-bold text-gray-900 mb-2">
            {farmerData?.marketAccess}%
          </div>
          <div className="text-gray-600 mb-2">
            {language === 'te' ? 'మార్కెట్ యాక్సెస్' : 'Market Access'}
          </div>
          <div className="text-sm text-gray-500">
            {language === 'te' ? 'సుధారణ' : 'improvement'}
          </div>
        </div>
      </motion.div>

      {/* Income Trends Chart */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.1 }}
        className="bg-white rounded-2xl shadow-lg p-6 mb-8"
      >
        <h3 className="text-xl font-bold text-gray-900 mb-4">
          {language === 'te' ? 'ఆదాయ ట్రెండ్' : 'Income Trend'}
        </h3>
        
        <div className="h-64 flex items-end justify-between space-x-2">
          {incomeTrends.map((trend, index) => {
            const maxIncome = Math.max(...incomeTrends.map(t => t.income));
            const height = (trend.income / maxIncome) * 200;
            
            return (
              <div key={trend.month} className="flex flex-col items-center flex-1">
                <div className="w-full bg-green-200 rounded-t-lg relative group">
                  <div 
                    className="bg-green-600 rounded-t-lg transition-all duration-500"
                    style={{ height: `${height}px` }}
                  ></div>
                  <div className="absolute -top-8 left-1/2 transform -translate-x-1/2 opacity-0 group-hover:opacity-100 transition-opacity bg-gray-800 text-white text-xs px-2 py-1 rounded">
                    {formatCurrency(trend.income)}
                  </div>
                </div>
                <div className="text-xs text-gray-600 mt-2">{trend.month}</div>
                <div className="text-xs text-gray-500">{trend.sales} {language === 'te' ? 'అమ్మకాలు' : 'sales'}</div>
              </div>
            );
          })}
        </div>
      </motion.div>

      {/* Market Participation Analytics */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.2 }}
        className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8"
      >
        <div className="bg-white rounded-2xl shadow-lg p-6">
          <h3 className="text-xl font-bold text-gray-900 mb-4">
            {language === 'te' ? 'మార్కెట్ పాల్గొనుట' : 'Market Participation'}
          </h3>
          
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <span className="text-gray-600">{language === 'te' ? 'మొత్తం లావాదేవీలు' : 'Total Transactions'}</span>
              <span className="font-semibold text-gray-900">{marketParticipation?.totalTransactions}</span>
            </div>
            
            <div className="flex items-center justify-between">
              <span className="text-gray-600">{language === 'te' ? 'విజయవంతమైన లావాదేవీలు' : 'Successful Transactions'}</span>
              <span className="font-semibold text-gray-900">{marketParticipation?.successfulTransactions}</span>
            </div>
            
            <div className="flex items-center justify-between">
              <span className="text-gray-600">{language === 'te' ? 'సగటు లావాదేవీ సమయం' : 'Avg Transaction Time'}</span>
              <span className="font-semibold text-gray-900">{marketParticipation?.averageTransactionTime} {language === 'te' ? 'రోజులు' : 'days'}</span>
            </div>
            
            <div className="flex items-center justify-between">
              <span className="text-gray-600">{language === 'te' ? 'ధర మెరుగుదల' : 'Price Improvement'}</span>
              <span className="font-semibold text-green-600">+{marketParticipation?.priceImprovement}%</span>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-2xl shadow-lg p-6">
          <h3 className="text-xl font-bold text-gray-900 mb-4">
            {language === 'te' ? 'మార్కెట్ రీచ్' : 'Market Reach'}
          </h3>
          
          <div className="space-y-3">
            {marketParticipation?.marketReach.map((market, index) => (
              <div key={market} className="flex items-center space-x-3">
                <div className={`w-3 h-3 rounded-full ${
                  index === 0 ? 'bg-green-500' : 
                  index === 1 ? 'bg-blue-500' : 
                  index === 2 ? 'bg-purple-500' : 'bg-orange-500'
                }`}></div>
                <span className="text-gray-700">{market}</span>
                <div className="flex-1 bg-gray-200 rounded-full h-2">
                  <div 
                    className={`h-2 rounded-full ${
                      index === 0 ? 'bg-green-500' : 
                      index === 1 ? 'bg-blue-500' : 
                      index === 2 ? 'bg-purple-500' : 'bg-orange-500'
                    }`}
                    style={{ width: `${(index + 1) * 25}%` }}
                  ></div>
                </div>
              </div>
            ))}
          </div>
          
          <div className="mt-4 pt-4 border-t border-gray-200">
            <div className="flex items-center justify-between">
              <span className="text-gray-600">{language === 'te' ? 'పాల్గొనుట స్కోరు' : 'Participation Score'}</span>
              <span className="text-2xl font-bold text-green-600">{marketParticipation?.participationScore}/100</span>
            </div>
          </div>
        </div>
      </motion.div>

      {/* Cooperative Benefits */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.3 }}
        className="bg-white rounded-2xl shadow-lg p-6 mb-8"
      >
        <h3 className="text-xl font-bold text-gray-900 mb-4">
          {language === 'te' ? 'కోఆపరేటివ్ ప్రయోజనాలు' : 'Cooperative Benefits'}
        </h3>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="text-center">
            <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-3">
              <CurrencyRupeeIcon className="h-8 w-8 text-green-600" />
            </div>
            <div className="text-2xl font-bold text-gray-900 mb-2">
              {formatCurrency(cooperativeParticipation?.totalSavings)}
            </div>
            <div className="text-gray-600">
              {language === 'te' ? 'మొత్తం సేవింగ్స్' : 'Total Savings'}
            </div>
          </div>
          
          <div className="text-center">
            <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-3">
              <ChartPieIcon className="h-8 w-8 text-blue-600" />
            </div>
            <div className="text-2xl font-bold text-gray-900 mb-2">
              {cooperativeParticipation?.groupOrders}
            </div>
            <div className="text-gray-600">
              {language === 'te' ? 'సమూహ ఆర్డర్లు' : 'Group Orders'}
            </div>
          </div>
          
          <div className="text-center">
            <div className="w-16 h-16 bg-purple-100 rounded-full flex items-center justify-center mx-auto mb-3">
              <UserIcon className="h-8 w-8 text-purple-600" />
            </div>
            <div className="text-2xl font-bold text-gray-900 mb-2">
              {cooperativeParticipation?.participationRate}%
            </div>
            <div className="text-gray-600">
              {language === 'te' ? 'పాల్గొనుట రేటు' : 'Participation Rate'}
            </div>
          </div>
        </div>
      </motion.div>

      {/* Impact Summary */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.4 }}
        className="bg-gradient-to-r from-green-500 to-blue-500 rounded-2xl p-6 text-white"
      >
        <h3 className="text-xl font-bold mb-4">
          {language === 'te' ? 'ప్రభావ సారాంశం' : 'Impact Summary'}
        </h3>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <h4 className="font-semibold mb-2">
              {language === 'te' ? 'ఆదాయ వృద్ధి' : 'Income Growth'}
            </h4>
            <p className="text-sm opacity-90">
              {language === 'te' 
                ? `మీ ఆదాయం ${farmerData?.incomeGrowth}% పెరిగింది, ఇది ₹${(farmerData?.currentIncome - farmerData?.previousIncome).toLocaleString()} అదనపు ఆదాయాన్ని సూచిస్తుంది.`
                : `Your income has grown by ${farmerData?.incomeGrowth}%, representing ₹${(farmerData?.currentIncome - farmerData?.previousIncome).toLocaleString()} additional income.`
              }
            </p>
          </div>
          
          <div>
            <h4 className="font-semibold mb-2">
              {language === 'te' ? 'మార్కెట్ యాక్సెస్' : 'Market Access'}
            </h4>
            <p className="text-sm opacity-90">
              {language === 'te' 
                ? `మీరు ${marketParticipation?.totalTransactions} లావాదేవీలలో పాల్గొన్నారు మరియు ${marketParticipation?.priceImprovement}% ధర మెరుగుదలను సాధించారు.`
                : `You participated in ${marketParticipation?.totalTransactions} transactions and achieved ${marketParticipation?.priceImprovement}% price improvement.`
              }
            </p>
          </div>
        </div>
      </motion.div>
    </AnimatedPage>
  );
};

export default ImpactAnalyticsPage;
