import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { dbHelpers } from '../../config/supabase';

const SupabaseTest = () => {
  const [connectionStatus, setConnectionStatus] = useState('Testing...');
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    testConnection();
  }, []);

  const testConnection = async () => {
    try {
      setConnectionStatus('Testing...');
      
      // Test 1: Get all submissions
      const { data: submissions, error: submissionsError } = await dbHelpers.getAllSubmissions();
      
      if (submissionsError) {
        throw submissionsError;
      }

      setConnectionStatus('✅ Connected Successfully!');
      setData({
        submissions: submissions?.length || 0,
        sampleData: submissions?.slice(0, 3) || []
      });
      setError(null);
    } catch (err) {
      setConnectionStatus('❌ Connection Failed');
      setError(err.message || 'Unknown error occurred');
      console.error('Supabase connection error:', err);
    }
  };

  const testCreateFarmer = async () => {
    try {
      const newFarmer = {
        phone_number: '9999999999',
        name: 'Test Farmer',
        location: 'Test Location',
        language: 'en'
      };

      const { error } = await dbHelpers.createFarmer(newFarmer);
      
      if (error) {
        throw error;
      }

      alert('✅ Test farmer created successfully!');
      testConnection(); // Refresh data
    } catch (err) {
      alert('❌ Error creating farmer: ' + err.message);
    }
  };

  const testCreateSubmission = async () => {
    try {
      const newSubmission = {
        farmer_id: 'test-farmer-id',
        farmer_name: 'Test Farmer',
        farmer_phone: '9999999999',
        location: 'Test Location',
        quantity: 5,
        quality: 'Grade A',
        price: 8000,
        total_value: 40000,
        description: 'Test submission'
      };

      const { error } = await dbHelpers.createSubmission(newSubmission);
      
      if (error) {
        throw error;
      }

      alert('✅ Test submission created successfully!');
      testConnection(); // Refresh data
    } catch (err) {
      alert('❌ Error creating submission: ' + err.message);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-blue-50 p-6">
      <div className="max-w-4xl mx-auto">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white rounded-2xl shadow-xl p-8"
        >
          <h1 className="text-3xl font-bold text-gray-900 mb-6 text-center">
            🧪 Supabase Connection Test
          </h1>

          {/* Connection Status */}
          <div className="mb-8 p-6 bg-gray-50 rounded-xl">
            <h2 className="text-xl font-semibold mb-4">Connection Status</h2>
            <div className="text-2xl font-bold mb-2">{connectionStatus}</div>
            {error && (
              <div className="text-red-600 bg-red-50 p-4 rounded-lg">
                <strong>Error:</strong> {error}
              </div>
            )}
          </div>

          {/* Data Display */}
          {data && (
            <div className="mb-8 p-6 bg-green-50 rounded-xl">
              <h2 className="text-xl font-semibold mb-4">Database Data</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="bg-white p-4 rounded-lg">
                  <h3 className="font-semibold text-green-800">Total Submissions</h3>
                  <div className="text-3xl font-bold text-green-600">{data.farmers}</div>
                </div>
                <div className="bg-white p-4 rounded-lg">
                  <h3 className="font-semibold text-blue-800">Sample Data</h3>
                  <div className="text-sm text-gray-600">
                    {data.sampleData.length > 0 ? 'Data found' : 'No data'}
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Test Buttons */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <button
              onClick={testCreateFarmer}
              className="bg-blue-600 text-white py-3 px-6 rounded-lg font-medium hover:bg-blue-700 transition-colors"
            >
              🧑‍🌾 Test Create Farmer
            </button>
            <button
              onClick={testCreateSubmission}
              className="bg-green-600 text-white py-3 px-6 rounded-lg font-medium hover:bg-green-700 transition-colors"
            >
              📝 Test Create Submission
            </button>
          </div>

          {/* Sample Data Display */}
          {data && data.sampleData.length > 0 && (
            <div className="mt-8 p-6 bg-blue-50 rounded-xl">
              <h2 className="text-xl font-semibold mb-4">Sample Data Preview</h2>
              <div className="space-y-3">
                {data.sampleData.map((item, index) => (
                  <div key={index} className="bg-white p-4 rounded-lg">
                    <div className="font-semibold">{item.farmer_name}</div>
                    <div className="text-sm text-gray-600">
                      Phone: {item.farmer_phone} | Location: {item.location}
                    </div>
                    <div className="text-sm text-gray-600">
                      Quantity: {item.quantity} | Price: ₹{item.price} | Status: {item.status}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Instructions */}
          <div className="mt-8 p-6 bg-yellow-50 rounded-xl">
            <h2 className="text-xl font-semibold mb-4">📋 Verification Steps</h2>
            <div className="space-y-2 text-sm">
              <div>1. ✅ Check connection status above</div>
              <div>2. ✅ Verify data is loading from Supabase</div>
              <div>3. ✅ Test creating new records</div>
              <div>4. ✅ Check Supabase dashboard for new data</div>
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default SupabaseTest;
