import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../../contexts/LanguageContext';
import {
  DocumentTextIcon,
  CheckCircleIcon,
  ClockIcon,
  ExclamationTriangleIcon,
  CurrencyRupeeIcon,
  UserIcon,
  ShieldCheckIcon,
  ArrowPathIcon,
  PlayIcon,
  PauseIcon
} from '@heroicons/react/24/outline';
import AnimatedPage from './AnimatedPage';

const SmartContractsPage = ({ onBack }) => {
  const [contracts, setContracts] = useState([]);
  const [newContract, setNewContract] = useState({
    buyer: '',
    seller: '',
    amount: '',
    quantity: '',
    quality: 'Grade A',
    deliveryDate: '',
    conditions: ''
  });
  const [loading, setLoading] = useState(false);
  const [selectedContract, setSelectedContract] = useState(null);
  const { language } = useLanguage();

  useEffect(() => {
    loadContracts();
  }, []);

  const loadContracts = () => {
    // Mock smart contracts data
    const mockContracts = [
      {
        id: 'contract_001',
        buyer: 'Export Company Ltd',
        seller: 'Rama Rao',
        amount: 50000,
        quantity: 5,
        quality: 'Export Grade',
        deliveryDate: '2024-02-15',
        status: 'active',
        createdAt: Date.now() - 86400000,
        conditions: [
          language === 'te' ? 'టర్మరిక్ ఎగుమతి గ్రేడ్ కావాలి' : 'Turmeric must be export grade',
          language === 'te' ? 'ప్యాకేజింగ్ ప్రమాణాలను పాటించాలి' : 'Must follow packaging standards',
          language === 'te' ? 'డెలివరీ తేదీని పాటించాలి' : 'Must meet delivery deadline'
        ],
        milestones: [
          { name: language === 'te' ? 'కాంట్రాక్ట్ సృష్టించబడింది' : 'Contract Created', completed: true, date: '2024-01-15' },
          { name: language === 'te' ? 'అడ్వాన్స్ చెల్లింపు' : 'Advance Payment', completed: true, date: '2024-01-16' },
          { name: language === 'te' ? 'ఉత్పత్తి సిద్ధం' : 'Production Ready', completed: false, date: '2024-02-10' },
          { name: language === 'te' ? 'డెలివరీ' : 'Delivery', completed: false, date: '2024-02-15' },
          { name: language === 'te' ? 'ఫైనల్ పేమెంట్' : 'Final Payment', completed: false, date: '2024-02-16' }
        ]
      },
      {
        id: 'contract_002',
        buyer: 'Local Trader',
        seller: 'Sita Devi',
        amount: 30000,
        quantity: 3,
        quality: 'Grade A',
        deliveryDate: '2024-02-10',
        status: 'completed',
        createdAt: Date.now() - 172800000,
        conditions: [
          language === 'te' ? 'గుణమైన టర్మరిక్ అవసరం' : 'Quality turmeric required',
          language === 'te' ? 'సరైన ప్యాకేజింగ్' : 'Proper packaging required'
        ],
        milestones: [
          { name: language === 'te' ? 'కాంట్రాక్ట్ సృష్టించబడింది' : 'Contract Created', completed: true, date: '2024-01-10' },
          { name: language === 'te' ? 'అడ్వాన్స్ చెల్లింపు' : 'Advance Payment', completed: true, date: '2024-01-11' },
          { name: language === 'te' ? 'ఉత్పత్తి సిద్ధం' : 'Production Ready', completed: true, date: '2024-02-05' },
          { name: language === 'te' ? 'డెలివరీ' : 'Delivery', completed: true, date: '2024-02-10' },
          { name: language === 'te' ? 'ఫైనల్ పేమెంట్' : 'Final Payment', completed: true, date: '2024-02-11' }
        ]
      }
    ];
    setContracts(mockContracts);
  };

  const createContract = async () => {
    if (!newContract.buyer || !newContract.seller || !newContract.amount || !newContract.quantity) {
      alert(language === 'te' ? 'దయచేసి అన్ని ఫీల్డ్లను పూరించండి' : 'Please fill all fields');
      return;
    }

    setLoading(true);
    try {
      const contract = {
        id: `contract_${Date.now()}`,
        ...newContract,
        amount: parseInt(newContract.amount),
        quantity: parseInt(newContract.quantity),
        status: 'active',
        createdAt: Date.now(),
        conditions: newContract.conditions.split('\n').filter(c => c.trim()),
        milestones: [
          { name: language === 'te' ? 'కాంట్రాక్ట్ సృష్టించబడింది' : 'Contract Created', completed: true, date: new Date().toISOString().split('T')[0] },
          { name: language === 'te' ? 'అడ్వాన్స్ చెల్లింపు' : 'Advance Payment', completed: false, date: '' },
          { name: language === 'te' ? 'ఉత్పత్తి సిద్ధం' : 'Production Ready', completed: false, date: '' },
          { name: language === 'te' ? 'డెలివరీ' : 'Delivery', completed: false, date: newContract.deliveryDate },
          { name: language === 'te' ? 'ఫైనల్ పేమెంట్' : 'Final Payment', completed: false, date: '' }
        ]
      };

      setContracts(prev => [contract, ...prev]);
      setNewContract({
        buyer: '',
        seller: '',
        amount: '',
        quantity: '',
        quality: 'Grade A',
        deliveryDate: '',
        conditions: ''
      });
      
      alert(language === 'te' ? 'స్మార్ట్ కాంట్రాక్ట్ సృష్టించబడింది!' : 'Smart contract created successfully!');
    } catch (error) {
      console.error('Error creating contract:', error);
      alert(language === 'te' ? 'కాంట్రాక్ట్ సృష్టించడంలో లోపం' : 'Error creating contract');
    } finally {
      setLoading(false);
    }
  };

  const executeMilestone = (contractId, milestoneIndex) => {
    setContracts(prev => prev.map(contract => 
      contract.id === contractId 
        ? {
            ...contract,
            milestones: contract.milestones.map((milestone, index) => 
              index === milestoneIndex 
                ? { ...milestone, completed: true, date: new Date().toISOString().split('T')[0] }
                : milestone
            )
          }
        : contract
    ));
    alert(language === 'te' ? 'మైల్‌స్టోన్ ఎక్సిక్యూట్ చేయబడింది!' : 'Milestone executed!');
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800';
      case 'completed': return 'bg-blue-100 text-blue-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'failed': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusText = (status) => {
    const statusMap = {
      active: language === 'te' ? 'క్రియాశీల' : 'Active',
      completed: language === 'te' ? 'పూర్తయింది' : 'Completed',
      pending: language === 'te' ? 'వేచి ఉంది' : 'Pending',
      failed: language === 'te' ? 'విఫలమైంది' : 'Failed'
    };
    return statusMap[status] || status;
  };

  const formatCurrency = (amount) => {
    if (!amount || amount === undefined || amount === null) {
      return '₹0';
    }
    return `₹${amount.toLocaleString()}`;
  };

  return (
    <AnimatedPage 
      title={language === 'te' ? 'స్మార్ట్ కాంట్రాక్ట్‌లు' : 'Smart Contracts'} 
      onBack={onBack} 
      icon={DocumentTextIcon}
    >
      {/* Create New Contract */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="bg-white rounded-2xl shadow-lg p-6 mb-8"
      >
        <h3 className="text-xl font-bold text-gray-900 mb-4">
          {language === 'te' ? 'కొత్త స్మార్ట్ కాంట్రాక్ట్ సృష్టించండి' : 'Create New Smart Contract'}
        </h3>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {language === 'te' ? 'కొనుగోలుదారు' : 'Buyer'}
            </label>
            <input
              type="text"
              value={newContract.buyer}
              onChange={(e) => setNewContract({ ...newContract, buyer: e.target.value })}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
              placeholder={language === 'te' ? 'కొనుగోలుదారు పేరు' : 'Buyer name'}
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {language === 'te' ? 'విక్రేత' : 'Seller'}
            </label>
            <input
              type="text"
              value={newContract.seller}
              onChange={(e) => setNewContract({ ...newContract, seller: e.target.value })}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
              placeholder={language === 'te' ? 'విక్రేత పేరు' : 'Seller name'}
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {language === 'te' ? 'మొత్తం (₹)' : 'Amount (₹)'}
            </label>
            <input
              type="number"
              value={newContract.amount}
              onChange={(e) => setNewContract({ ...newContract, amount: e.target.value })}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
              placeholder={language === 'te' ? 'మొత్తం నమోదు చేయండి' : 'Enter amount'}
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {language === 'te' ? 'పరిమాణం (క్వింటాళ్లు)' : 'Quantity (Quintals)'}
            </label>
            <input
              type="number"
              value={newContract.quantity}
              onChange={(e) => setNewContract({ ...newContract, quantity: e.target.value })}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
              placeholder={language === 'te' ? 'పరిమాణం నమోదు చేయండి' : 'Enter quantity'}
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {language === 'te' ? 'నాణ్యత' : 'Quality'}
            </label>
            <select
              value={newContract.quality}
              onChange={(e) => setNewContract({ ...newContract, quality: e.target.value })}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
            >
              <option value="Grade A">{language === 'te' ? 'గ్రేడ్ A' : 'Grade A'}</option>
              <option value="Export Grade">{language === 'te' ? 'ఎగుమతి గ్రేడ్' : 'Export Grade'}</option>
              <option value="Premium">{language === 'te' ? 'ప్రీమియం' : 'Premium'}</option>
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {language === 'te' ? 'డెలివరీ తేదీ' : 'Delivery Date'}
            </label>
            <input
              type="date"
              value={newContract.deliveryDate}
              onChange={(e) => setNewContract({ ...newContract, deliveryDate: e.target.value })}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
            />
          </div>
        </div>
        
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            {language === 'te' ? 'కాంట్రాక్ట్ షరతులు' : 'Contract Conditions'}
          </label>
          <textarea
            value={newContract.conditions}
            onChange={(e) => setNewContract({ ...newContract, conditions: e.target.value })}
            rows={3}
            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
            placeholder={language === 'te' ? 'షరతులను ప్రతి లైన్‌లో ఒక్కటి చొప్పున నమోదు చేయండి' : 'Enter conditions, one per line'}
          />
        </div>
        
        <button
          onClick={createContract}
          disabled={loading}
          className="w-full bg-green-600 text-white py-3 px-6 rounded-lg font-semibold hover:bg-green-700 transition-colors disabled:opacity-50"
        >
          {loading ? (
            <div className="flex items-center justify-center space-x-2">
              <ArrowPathIcon className="h-5 w-5 animate-spin" />
              <span>{language === 'te' ? 'సృష్టిస్తోంది...' : 'Creating...'}</span>
            </div>
          ) : (
            language === 'te' ? 'స్మార్ట్ కాంట్రాక్ట్ సృష్టించండి' : 'Create Smart Contract'
          )}
        </button>
      </motion.div>

      {/* Contracts List */}
      <div className="space-y-6">
        {contracts.map((contract, index) => (
          <motion.div
            key={contract.id}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: index * 0.1 }}
            className="bg-white rounded-2xl shadow-lg p-6"
          >
            <div className="flex items-center justify-between mb-4">
              <div>
                <h3 className="text-lg font-semibold text-gray-900">{contract.id}</h3>
                <p className="text-sm text-gray-600">
                  {contract.buyer} ↔ {contract.seller}
                </p>
              </div>
              <span className={`px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(contract.status)}`}>
                {getStatusText(contract.status)}
              </span>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
              <div className="text-center">
                <div className="text-2xl font-bold text-gray-900">{formatCurrency(contract.amount)}</div>
                <div className="text-sm text-gray-600">
                  {language === 'te' ? 'మొత్తం' : 'Total Amount'}
                </div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-gray-900">{contract.quantity}</div>
                <div className="text-sm text-gray-600">
                  {language === 'te' ? 'క్వింటాళ్లు' : 'Quintals'}
                </div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-gray-900">{contract.quality}</div>
                <div className="text-sm text-gray-600">
                  {language === 'te' ? 'నాణ్యత' : 'Quality'}
                </div>
              </div>
            </div>

            <div className="mb-4">
              <h4 className="font-semibold text-gray-900 mb-2">
                {language === 'te' ? 'కాంట్రాక్ట్ షరతులు' : 'Contract Conditions'}
              </h4>
              <ul className="text-sm text-gray-600 space-y-1">
                {contract.conditions.map((condition, idx) => (
                  <li key={idx} className="flex items-start space-x-2">
                    <CheckCircleIcon className="h-4 w-4 text-green-500 mt-0.5 flex-shrink-0" />
                    <span>{condition}</span>
                  </li>
                ))}
              </ul>
            </div>

            <div className="mb-4">
              <h4 className="font-semibold text-gray-900 mb-2">
                {language === 'te' ? 'మైల్‌స్టోన్‌లు' : 'Milestones'}
              </h4>
              <div className="space-y-2">
                {contract.milestones.map((milestone, idx) => (
                  <div key={idx} className="flex items-center justify-between p-2 bg-gray-50 rounded-lg">
                    <div className="flex items-center space-x-3">
                      {milestone.completed ? (
                        <CheckCircleIcon className="h-5 w-5 text-green-500" />
                      ) : (
                        <ClockIcon className="h-5 w-5 text-gray-400" />
                      )}
                      <span className={`text-sm ${milestone.completed ? 'text-green-700' : 'text-gray-600'}`}>
                        {milestone.name}
                      </span>
                    </div>
                    <div className="flex items-center space-x-2">
                      {milestone.date && (
                        <span className="text-xs text-gray-500">{milestone.date}</span>
                      )}
                      {!milestone.completed && contract.status === 'active' && (
                        <button
                          onClick={() => executeMilestone(contract.id, idx)}
                          className="px-2 py-1 bg-green-600 text-white rounded text-xs hover:bg-green-700 transition-colors"
                        >
                          {language === 'te' ? 'ఎక్సిక్యూట్' : 'Execute'}
                        </button>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>

            <div className="flex items-center justify-between text-sm text-gray-500">
              <span>
                {language === 'te' ? 'సృష్టించబడిన తేదీ' : 'Created'}: {new Date(contract.createdAt).toLocaleDateString()}
              </span>
              <span>
                {language === 'te' ? 'డెలివరీ తేదీ' : 'Delivery'}: {contract.deliveryDate}
              </span>
            </div>
          </motion.div>
        ))}
      </div>

      {/* Smart Contract Benefits */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.4 }}
        className="mt-8 bg-blue-50 border border-blue-200 rounded-xl p-6"
      >
        <h3 className="text-lg font-semibold text-blue-800 mb-2">
          {language === 'te' ? 'స్మార్ట్ కాంట్రాక్ట్‌ల ప్రయోజనాలు' : 'Smart Contract Benefits'}
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-blue-700">
          <div className="flex items-center space-x-2">
            <ShieldCheckIcon className="h-5 w-5" />
            <span>{language === 'te' ? 'సురక్షిత లావాదేవీలు' : 'Secure Transactions'}</span>
          </div>
          <div className="flex items-center space-x-2">
            <CheckCircleIcon className="h-5 w-5" />
            <span>{language === 'te' ? 'స్వయంచాలక నిర్వహణ' : 'Automated Execution'}</span>
          </div>
          <div className="flex items-center space-x-2">
            <DocumentTextIcon className="h-5 w-5" />
            <span>{language === 'te' ? 'పారదర్శకత' : 'Transparency'}</span>
          </div>
          <div className="flex items-center space-x-2">
            <ClockIcon className="h-5 w-5" />
            <span>{language === 'te' ? 'సమయానుసారం నిర్వహణ' : 'Time-based Execution'}</span>
          </div>
        </div>
      </motion.div>
    </AnimatedPage>
  );
};

export default SmartContractsPage;
