import React, { useState } from 'react';
import { useLanguage } from '../../contexts/LanguageContext';
import { PhoneIcon, KeyIcon } from '@heroicons/react/24/outline';
import { supabase, dbHelpers } from '../../config/supabase';
import toast from 'react-hot-toast';

const LoginForm = ({ onLogin, onGovernmentLogin }) => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [step, setStep] = useState('phone'); // 'phone' or 'otp'
  const [loading, setLoading] = useState(false);
  const { language, changeLanguage } = useLanguage();

  const handlePhoneSubmit = async (e) => {
    e.preventDefault();
    if (!phoneNumber || phoneNumber.length !== 10) {
      toast.error(language === 'te' ? '‡∞¶‡∞Ø‡∞ö‡±á‡∞∏‡∞ø ‡∞ö‡±Ü‡∞≤‡±ç‡∞≤‡±Å‡∞¨‡∞æ‡∞ü‡±Å ‡∞Ö‡∞Ø‡±ç‡∞Ø‡±á 10 ‡∞Ö‡∞Ç‡∞ï‡±Ü‡∞≤ ‡∞´‡±ã‡∞®‡±ç ‡∞®‡∞Ç‡∞¨‡∞∞‡±ç ‡∞®‡∞Æ‡±ã‡∞¶‡±Å ‡∞ö‡±á‡∞Ø‡∞Ç‡∞°‡∞ø' : 'Please enter a valid 10-digit phone number');
      return;
    }

    setLoading(true);
    try {
      // Send OTP using Supabase Auth with Twilio
      console.log('Sending OTP to:', `+91${phoneNumber}`);
      
      const { data, error } = await supabase.auth.signInWithOtp({
        phone: `+91${phoneNumber}`,
        options: {
          channel: 'sms'
        }
      });

      console.log('Supabase response:', { data, error });

      if (error) {
        console.error('Supabase OTP error:', error);
        throw error;
      }

      toast.success(language === 'te' 
        ? `OTP ‡∞™‡∞Ç‡∞™‡∞¨‡∞°‡∞ø‡∞Ç‡∞¶‡∞ø: ${phoneNumber}` 
        : `OTP sent to: ${phoneNumber}`
      );
      setStep('otp');
    } catch (error) {
      console.error('OTP send error:', error);
      
      // More detailed error messages
      let errorMessage = error.message || (language === 'te' ? 'OTP ‡∞™‡∞Ç‡∞™‡∞°‡∞Ç‡∞≤‡±ã ‡∞≤‡±ã‡∞™‡∞Ç' : 'Failed to send OTP. Please try again.');
      
      if (error.message?.includes('Invalid phone number')) {
        errorMessage = language === 'te' ? '‡∞ö‡±Ü‡∞≤‡±ç‡∞≤‡∞®‡∞ø ‡∞´‡±ã‡∞®‡±ç ‡∞®‡∞Ç‡∞¨‡∞∞‡±ç. ‡∞¶‡∞Ø‡∞ö‡±á‡∞∏‡∞ø ‡∞Æ‡∞≥‡±ç‡∞≤‡±Ä ‡∞™‡±ç‡∞∞‡∞Ø‡∞§‡±ç‡∞®‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø.' : 'Invalid phone number. Please try again.';
      } else if (error.message?.includes('rate limit')) {
        errorMessage = language === 'te' ? '‡∞ö‡∞æ‡∞≤‡∞æ ‡∞é‡∞ï‡±ç‡∞ï‡±Å‡∞µ ‡∞™‡±ç‡∞∞‡∞Ø‡∞§‡±ç‡∞®‡∞æ‡∞≤‡±Å. ‡∞ï‡±ä‡∞Ç‡∞§ ‡∞∏‡∞Æ‡∞Ø‡∞Ç ‡∞§‡∞∞‡±ç‡∞µ‡∞æ‡∞§ ‡∞™‡±ç‡∞∞‡∞Ø‡∞§‡±ç‡∞®‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø.' : 'Too many attempts. Please try again later.';
      } else if (error.message?.includes('provider')) {
        errorMessage = language === 'te' ? 'SMS ‡∞™‡±ç‡∞∞‡±ä‡∞µ‡±à‡∞°‡∞∞‡±ç ‡∞∏‡∞Æ‡∞∏‡±ç‡∞Ø. ‡∞¶‡∞Ø‡∞ö‡±á‡∞∏‡∞ø ‡∞Æ‡∞≥‡±ç‡∞≤‡±Ä ‡∞™‡±ç‡∞∞‡∞Ø‡∞§‡±ç‡∞®‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø.' : 'SMS provider issue. Please try again.';
      }
      
      toast.error(errorMessage);
      
      // Fallback: Offer demo OTP for testing
      if (error.message?.includes('provider') || error.message?.includes('Invalid phone number') || error.message?.includes('expired') || error.message?.includes('invalid')) {
        setTimeout(() => {
          const useDemo = window.confirm(
            language === 'te' 
              ? 'OTP ‡∞∏‡∞Æ‡∞∏‡±ç‡∞Ø ‡∞â‡∞Ç‡∞¶‡∞ø. ‡∞°‡±Ü‡∞Æ‡±ã OTP ‡∞â‡∞™‡∞Ø‡±ã‡∞ó‡∞ø‡∞Ç‡∞ö‡∞æ‡∞≤‡∞æ? (123456)'
              : 'OTP issue detected. Use demo OTP? (123456)'
          );
          
          if (useDemo) {
            localStorage.setItem('demo_otp', '123456');
            localStorage.setItem('demo_phone', phoneNumber);
            toast.success(language === 'te' ? '‡∞°‡±Ü‡∞Æ‡±ã OTP: 123456' : 'Demo OTP: 123456');
            setStep('otp');
          }
        }, 2000);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleOtpSubmit = async (e) => {
    e.preventDefault();
    if (!otp || otp.length !== 6) {
      toast.error(language === 'te' ? '‡∞¶‡∞Ø‡∞ö‡±á‡∞∏‡∞ø ‡∞ö‡±Ü‡∞≤‡±ç‡∞≤‡±Å‡∞¨‡∞æ‡∞ü‡±Å ‡∞Ö‡∞Ø‡±ç‡∞Ø‡±á 6 ‡∞Ö‡∞Ç‡∞ï‡±Ü‡∞≤ OTP ‡∞®‡∞Æ‡±ã‡∞¶‡±Å ‡∞ö‡±á‡∞Ø‡∞Ç‡∞°‡∞ø' : 'Please enter a valid 6-digit OTP');
      return;
    }

    setLoading(true);
    try {
      // Check if this is a demo OTP first
      const storedOTP = localStorage.getItem('demo_otp');
      const storedPhone = localStorage.getItem('demo_phone');
      
      if (otp === storedOTP && phoneNumber === storedPhone) {
        // Demo OTP verification
        console.log('Using demo OTP verification');
        localStorage.removeItem('demo_otp');
        localStorage.removeItem('demo_phone');
      } else {
        // Real Supabase OTP verification
        console.log('Verifying real OTP with Supabase');
        const { data, error } = await supabase.auth.verifyOtp({
          phone: `+91${phoneNumber}`,
          token: otp,
          type: 'sms'
        });

        if (error) {
          console.error('Supabase OTP verification error:', error);
          throw error;
        }
      }

      // Get or create farmer profile
      let farmerProfile = await dbHelpers.getFarmer(phoneNumber);
      
      if (!farmerProfile.data) {
        // Create new farmer profile with auto-filled data
        const newFarmer = {
          phone_number: phoneNumber,
          name: `Farmer ${phoneNumber.slice(-4)}`, // Default name
          location: 'Unknown',
          language: language,
          created_at: new Date().toISOString(),
          last_login: new Date().toISOString()
        };
        
        const { data: createdFarmer, error: createError } = await dbHelpers.createFarmer(newFarmer);
        
        if (createError) {
          throw createError;
        }
        
        farmerProfile = { data: createdFarmer[0] };
        toast.success(language === 'te' ? `‡∞ï‡±ä‡∞§‡±ç‡∞§ ‡∞™‡±ç‡∞∞‡±ä‡∞´‡±à‡∞≤‡±ç ‡∞∏‡±É‡∞∑‡±ç‡∞ü‡∞ø‡∞Ç‡∞ö‡∞¨‡∞°‡∞ø‡∞Ç‡∞¶‡∞ø! (${phoneNumber})` : `New profile created! (${phoneNumber})`);
      } else {
        // Update last login for existing farmer
        const { error: updateError } = await dbHelpers.updateFarmer(phoneNumber, {
          last_login: new Date().toISOString()
        });
        
        if (updateError) {
          console.warn('Failed to update last login:', updateError);
        }
        
        toast.success(language === 'te' ? `‡∞∏‡±ç‡∞µ‡∞æ‡∞ó‡∞§‡∞Ç! ${farmerProfile.data.name}` : `Welcome back! ${farmerProfile.data.name}`);
      }

      // Login successful with auto-filled profile
      const userData = {
        id: farmerProfile.data.id,
        phoneNumber: farmerProfile.data.phone_number,
        name: farmerProfile.data.name,
        location: farmerProfile.data.location,
        language: farmerProfile.data.language,
        loginTime: new Date().toISOString()
      };

      onLogin(userData);
      toast.success(language === 'te' ? '‡∞≤‡∞æ‡∞ó‡∞ø‡∞®‡±ç ‡∞µ‡∞ø‡∞ú‡∞Ø‡∞µ‡∞Ç‡∞§‡∞Ç!' : 'Login successful!');
      
    } catch (error) {
      console.error('OTP verify error:', error);
      toast.error(error.message || (language === 'te' ? 'OTP ‡∞ß‡±É‡∞µ‡±Ä‡∞ï‡∞∞‡∞£‡∞≤‡±ã ‡∞≤‡±ã‡∞™‡∞Ç' : 'Failed to verify OTP. Please try again.'));
    } finally {
      setLoading(false);
    }
  };

  const handleLanguageToggle = () => {
    const newLanguage = language === 'en' ? 'te' : 'en';
    changeLanguage(newLanguage);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary-50 to-turmeric-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-md">
        {/* Language Toggle */}
        <div className="flex justify-end mb-4">
          <button 
            onClick={handleLanguageToggle}
            className="px-3 py-1 bg-gray-100 rounded-lg text-gray-700 hover:bg-gray-200 transition-colors text-sm font-semibold"
          >
            {language === 'te' ? 'English' : '‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å'}
          </button>
        </div>

        <div className="text-center mb-8">
          <div className="w-16 h-16 bg-turmeric-500 rounded-full flex items-center justify-center mx-auto mb-4">
            <span className="text-2xl">üå±</span>
          </div>
          <h1 className="text-2xl font-bold text-gray-900 mb-2">TurmerLink</h1>
          <p className="text-gray-600">{language === 'te' ? '‡∞µ‡±ç‡∞Ø‡∞µ‡∞∏‡∞æ‡∞Ø‡∞¶‡∞æ‡∞∞‡±Å‡∞≤ ‡∞Æ‡∞æ‡∞∞‡±ç‡∞ï‡±Ü‡∞ü‡±ç ‡∞Ø‡∞æ‡∞ï‡±ç‡∞∏‡±Ü‡∞∏‡±ç ‡∞™‡±ç‡∞≤‡∞æ‡∞ü‡±ç‚Äå‡∞´‡∞æ‡∞∞‡∞Æ‡±ç' : 'Farmers Market Access Platform'}</p>
        </div>

        {step === 'phone' ? (
          <form onSubmit={handlePhoneSubmit} className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                {language === 'te' ? '‡∞´‡±ã‡∞®‡±ç ‡∞®‡∞Ç‡∞¨‡∞∞‡±ç' : 'Phone Number'}
              </label>
              <div className="relative">
                <PhoneIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                <input
                  type="tel"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  placeholder={language === 'te' ? '10 ‡∞Ö‡∞Ç‡∞ï‡±Ü‡∞≤ ‡∞´‡±ã‡∞®‡±ç ‡∞®‡∞Ç‡∞¨‡∞∞‡±ç ‡∞®‡∞Æ‡±ã‡∞¶‡±Å ‡∞ö‡±á‡∞Ø‡∞Ç‡∞°‡∞ø' : 'Enter 10-digit phone number'}
                  className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                  maxLength="10"
                />
              </div>
            </div>

            <button
              type="submit"
              disabled={loading}
              className="w-full bg-primary-600 text-white py-3 px-4 rounded-lg font-medium hover:bg-primary-700 focus:ring-2 focus:ring-primary-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              {loading ? (language === 'te' ? '‡∞≤‡±ã‡∞°‡±ç ‡∞Ö‡∞µ‡±Å‡∞§‡±ã‡∞Ç‡∞¶‡∞ø' : 'Loading...') : (language === 'te' ? 'OTP ‡∞™‡∞Ç‡∞™‡∞Ç‡∞°‡∞ø' : 'Send OTP')}
            </button>
            
            {/* Demo OTP Button for Testing */}
            <button
              type="button"
              onClick={() => {
                localStorage.setItem('demo_otp', '123456');
                localStorage.setItem('demo_phone', phoneNumber);
                toast.success(language === 'te' ? '‡∞°‡±Ü‡∞Æ‡±ã OTP: 123456' : 'Demo OTP: 123456');
                setStep('otp');
              }}
              className="w-full bg-yellow-500 text-white py-2 px-4 rounded-lg font-medium hover:bg-yellow-600 transition-colors text-sm"
            >
              {language === 'te' ? '‡∞°‡±Ü‡∞Æ‡±ã OTP (123456)' : 'Demo OTP (123456)'}
            </button>
            
            {/* Profile Info */}
            <div className="mt-3 p-3 bg-green-50 rounded-lg border border-green-200">
              <p className="text-sm text-green-800 font-medium">
                {language === 'te' ? 'üì± ‡∞™‡±ç‡∞∞‡∞§‡∞ø ‡∞´‡±ã‡∞®‡±ç ‡∞®‡∞Ç‡∞¨‡∞∞‡±ç ‡∞ï‡±Å ‡∞µ‡±á‡∞∞‡±á ‡∞™‡±ç‡∞∞‡±ä‡∞´‡±à‡∞≤‡±ç' : 'üì± Each phone number gets its own profile'}
              </p>
              <p className="text-xs text-green-600 mt-1">
                {language === 'te' ? '‡∞°‡±á‡∞ü‡∞æ‡∞¨‡±á‡∞∏‡±ç ‡∞≤‡±ã ‡∞®‡∞ø‡∞≤‡±ç‡∞µ ‡∞ö‡±á‡∞Ø‡∞¨‡∞°‡±Å‡∞§‡±Å‡∞Ç‡∞¶‡∞ø' : 'Stored in Supabase database'}
              </p>
            </div>
          </form>
        ) : (
          <form onSubmit={handleOtpSubmit} className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                {language === 'te' ? 'OTP ‡∞®‡∞Æ‡±ã‡∞¶‡±Å ‡∞ö‡±á‡∞Ø‡∞Ç‡∞°‡∞ø' : 'Enter OTP'}
              </label>
              <div className="relative">
                <KeyIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                <input
                  type="text"
                  value={otp}
                  onChange={(e) => setOtp(e.target.value)}
                  placeholder={language === 'te' ? '6 ‡∞Ö‡∞Ç‡∞ï‡±Ü‡∞≤ OTP ‡∞®‡∞Æ‡±ã‡∞¶‡±Å ‡∞ö‡±á‡∞Ø‡∞Ç‡∞°‡∞ø' : 'Enter 6-digit OTP'}
                  className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                  maxLength="6"
                />
              </div>
              <p className="text-sm text-gray-500 mt-2">
                {language === 'te' ? `OTP ‡∞™‡∞Ç‡∞™‡∞¨‡∞°‡∞ø‡∞Ç‡∞¶‡∞ø +91 ${phoneNumber}` : `OTP sent to +91 ${phoneNumber}`}
              </p>
                <div className="mt-3 p-3 bg-yellow-50 rounded-lg border border-yellow-200">
                <p className="text-sm text-yellow-800 font-medium">
                  {language === 'te' ? '‚ö†Ô∏è SMS ‡∞∏‡∞Æ‡∞∏‡±ç‡∞Ø ‡∞ï‡∞æ‡∞∞‡∞£‡∞Ç‡∞ó‡∞æ, ‡∞¶‡∞Ø‡∞ö‡±á‡∞∏‡∞ø ‡∞°‡±Ü‡∞Æ‡±ã OTP ‡∞¨‡∞ü‡∞®‡±ç ‡∞â‡∞™‡∞Ø‡±ã‡∞ó‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø' : '‚ö†Ô∏è Due to SMS issues, please use the Demo OTP button below'}
                </p>
              </div>
              
              <div className="mt-2 p-3 bg-blue-50 rounded-lg border border-blue-200">
                <p className="text-sm text-blue-800 font-medium">
                  {language === 'te' ? 'üì± ‡∞™‡±ç‡∞∞‡∞§‡∞ø ‡∞´‡±ã‡∞®‡±ç ‡∞®‡∞Ç‡∞¨‡∞∞‡±ç ‡∞ï‡±Å ‡∞µ‡±á‡∞∞‡±á ‡∞™‡±ç‡∞∞‡±ä‡∞´‡±à‡∞≤‡±ç ‡∞∏‡±É‡∞∑‡±ç‡∞ü‡∞ø‡∞Ç‡∞ö‡∞¨‡∞°‡±Å‡∞§‡±Å‡∞Ç‡∞¶‡∞ø' : 'üì± Each phone number gets its own individual profile'}
                </p>
                <p className="text-xs text-blue-600 mt-1">
                  {language === 'te' ? `‡∞´‡±ã‡∞®‡±ç: ${phoneNumber} - ‡∞°‡±á‡∞ü‡∞æ‡∞¨‡±á‡∞∏‡±ç ‡∞≤‡±ã ‡∞®‡∞ø‡∞≤‡±ç‡∞µ ‡∞ö‡±á‡∞Ø‡∞¨‡∞°‡±Å‡∞§‡±Å‡∞Ç‡∞¶‡∞ø` : `Phone: ${phoneNumber} - Stored in database`}
                </p>
              </div>
            </div>

            {/* Demo OTP Button - Prominent */}
            <button
              type="button"
              onClick={() => {
                localStorage.setItem('demo_otp', '123456');
                localStorage.setItem('demo_phone', phoneNumber);
                toast.success(language === 'te' ? '‡∞°‡±Ü‡∞Æ‡±ã OTP: 123456' : 'Demo OTP: 123456');
                setStep('otp');
              }}
              className="w-full bg-green-500 text-white py-3 px-4 rounded-lg font-medium hover:bg-green-600 transition-colors mb-3"
            >
              {language === 'te' ? '‚úÖ ‡∞°‡±Ü‡∞Æ‡±ã OTP ‡∞â‡∞™‡∞Ø‡±ã‡∞ó‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø (123456)' : '‚úÖ Use Demo OTP (123456)'}
            </button>

            <div className="flex space-x-3">
              <button
                type="button"
                onClick={() => setStep('phone')}
                className="flex-1 bg-gray-200 text-gray-800 py-3 px-4 rounded-lg font-medium hover:bg-gray-300 transition-colors"
              >
                {language === 'te' ? '‡∞∞‡∞¶‡±ç‡∞¶‡±Å ‡∞ö‡±á‡∞Ø‡∞Ç‡∞°‡∞ø' : 'Cancel'}
              </button>
              <button
                type="submit"
                disabled={loading}
                className="flex-1 bg-primary-600 text-white py-3 px-4 rounded-lg font-medium hover:bg-primary-700 focus:ring-2 focus:ring-primary-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                {loading ? (language === 'te' ? '‡∞≤‡±ã‡∞°‡±ç ‡∞Ö‡∞µ‡±Å‡∞§‡±ã‡∞Ç‡∞¶‡∞ø' : 'Loading...') : (language === 'te' ? '‡∞ß‡±É‡∞µ‡±Ä‡∞ï‡∞∞‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø' : 'Verify')}
              </button>
            </div>
          </form>
        )}
        
        {/* Government Login Option */}
        <div className="mt-6 pt-6 border-t border-gray-200">
          <div className="text-center">
            <p className="text-sm text-gray-600 mb-3">
              {language === 'te' ? '‡∞™‡±ç‡∞∞‡∞≠‡±Å‡∞§‡±ç‡∞µ ‡∞Ö‡∞ß‡∞ø‡∞ï‡∞æ‡∞∞‡±Å‡∞≤‡∞ï‡±Å' : 'For Government Officials'}
            </p>
            <button
              onClick={onGovernmentLogin}
              className="w-full bg-blue-600 text-white py-3 px-4 rounded-lg font-medium hover:bg-blue-700 transition-colors flex items-center justify-center space-x-2"
            >
              <span>üèõÔ∏è</span>
              <span>{language === 'te' ? '‡∞™‡±ç‡∞∞‡∞≠‡±Å‡∞§‡±ç‡∞µ ‡∞≤‡∞æ‡∞ó‡∞ø‡∞®‡±ç' : 'Government Login'}</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
