import React, { useState } from 'react';
import { AnimatePresence } from 'framer-motion';
import SimpleRuralHomePage from '../Layout/SimpleRuralHomePage';
import MarketPricesPage from '../Pages/MarketPricesPage';
import MySalesPage from '../Pages/MySalesPage';
import LearningPage from '../Pages/LearningPage';
import WeatherPage from '../Pages/WeatherPage';
import MSPPage from '../Pages/MSPPage';
import NotificationsPage from '../Pages/NotificationsPage';
import LoginForm from '../Auth/LoginForm';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [user, setUser] = useState(null);

  // Get language from localStorage or default to 'en'
  const [language, setLanguage] = useState(() => {
    const savedLanguage = localStorage.getItem('turmerlink_language');
    return savedLanguage || 'en';
  });

  const handleNavigate = (page) => {
    setCurrentPage(page);
  };

  const handleBack = () => {
    setCurrentPage('home');
  };

  const handleLogin = (userData) => {
    setUser(userData);
    setCurrentPage('home');
  };

  const handleLogout = () => {
    setUser(null);
    setCurrentPage('home');
  };

  const handleLanguageChange = (newLanguage) => {
    setLanguage(newLanguage);
    localStorage.setItem('turmerlink_language', newLanguage);
  };

  if (!user) {
    return <LoginForm onLogin={handleLogin} />;
  }

  return (
    <div className="min-h-screen">
      <AnimatePresence mode="wait">
        {currentPage === 'home' && (
          <SimpleRuralHomePage key="home" onNavigate={handleNavigate} language={language} onLanguageChange={handleLanguageChange} />
        )}
        {currentPage === 'dashboard' && (
          <MarketPricesPage key="dashboard" onBack={handleBack} language={language} />
        )}
        {currentPage === 'ledger' && (
          <MySalesPage key="ledger" onBack={handleBack} language={language} />
        )}
        {currentPage === 'learning' && (
          <LearningPage key="learning" onBack={handleBack} language={language} />
        )}
        {currentPage === 'weather' && (
          <WeatherPage key="weather" onBack={handleBack} language={language} />
        )}
        {currentPage === 'msp' && (
          <MSPPage key="msp" onBack={handleBack} language={language} />
        )}
        {currentPage === 'notifications' && (
          <NotificationsPage key="notifications" onBack={handleBack} language={language} />
        )}
      </AnimatePresence>

      {/* User Info & Logout */}
      <div className="fixed top-4 right-4 z-40">
        <div className="bg-white rounded-lg shadow-lg p-3 flex items-center space-x-3">
          <div className="w-8 h-8 bg-primary-100 rounded-full flex items-center justify-center">
            <span className="text-primary-600 font-semibold text-sm">
              {user?.name?.charAt(0) || 'F'}
            </span>
          </div>
          <div>
            <div className="text-sm font-medium text-gray-900">{user?.name}</div>
            <div className="text-xs text-gray-500">{user?.phoneNumber}</div>
          </div>
          <button
            onClick={handleLogout}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h7a3 3 0 013 3v1" />
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
};

export default App;
