import React, { useState } from 'react';
import { useLanguage } from '../../contexts/LanguageContext';
import { PhoneIcon, KeyIcon } from '@heroicons/react/24/outline';
import { supabase, dbHelpers } from '../../config/supabase';
import toast from 'react-hot-toast';

const LoginForm = ({ onLogin, onGovernmentLogin }) => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [step, setStep] = useState('phone'); // 'phone' or 'otp'
  const [loading, setLoading] = useState(false);
  const { language, changeLanguage } = useLanguage();

  const handlePhoneSubmit = async (e) => {
    e.preventDefault();
    if (!phoneNumber || phoneNumber.length !== 10) {
      toast.error(language === 'te' ? 'దయచేసి చెల్లుబాటు అయ్యే 10 అంకెల ఫోన్ నంబర్ నమోదు చేయండి' : 'Please enter a valid 10-digit phone number');
      return;
    }

    setLoading(true);
    try {
      // Send OTP using Supabase Auth with Twilio
      console.log('Sending OTP to:', `+91${phoneNumber}`);
      
      const { data, error } = await supabase.auth.signInWithOtp({
        phone: `+91${phoneNumber}`,
        options: {
          channel: 'sms'
        }
      });

      console.log('Supabase response:', { data, error });

      if (error) {
        console.error('Supabase OTP error:', error);
        throw error;
      }

      toast.success(language === 'te' 
        ? `OTP పంపబడింది: ${phoneNumber}` 
        : `OTP sent to: ${phoneNumber}`
      );
      setStep('otp');
    } catch (error) {
      console.error('OTP send error:', error);
      
      // More detailed error messages
      let errorMessage = error.message || (language === 'te' ? 'OTP పంపడంలో లోపం' : 'Failed to send OTP. Please try again.');
      
      if (error.message?.includes('Invalid phone number')) {
        errorMessage = language === 'te' ? 'చెల్లని ఫోన్ నంబర్. దయచేసి మళ్లీ ప్రయత్నించండి.' : 'Invalid phone number. Please try again.';
      } else if (error.message?.includes('rate limit')) {
        errorMessage = language === 'te' ? 'చాలా ఎక్కువ ప్రయత్నాలు. కొంత సమయం తర్వాత ప్రయత్నించండి.' : 'Too many attempts. Please try again later.';
      } else if (error.message?.includes('provider')) {
        errorMessage = language === 'te' ? 'SMS ప్రొవైడర్ సమస్య. దయచేసి మళ్లీ ప్రయత్నించండి.' : 'SMS provider issue. Please try again.';
      }
      
      toast.error(errorMessage);
      
      // Fallback: Offer demo OTP for testing
      if (error.message?.includes('provider') || error.message?.includes('Invalid phone number')) {
        setTimeout(() => {
          const useDemo = window.confirm(
            language === 'te' 
              ? 'SMS పంపడంలో సమస్య ఉంది. డెమో OTP ఉపయోగించాలా? (123456)'
              : 'SMS sending failed. Use demo OTP? (123456)'
          );
          
          if (useDemo) {
            localStorage.setItem('demo_otp', '123456');
            localStorage.setItem('demo_phone', phoneNumber);
            toast.success(language === 'te' ? 'డెమో OTP: 123456' : 'Demo OTP: 123456');
            setStep('otp');
          }
        }, 2000);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleOtpSubmit = async (e) => {
    e.preventDefault();
    if (!otp || otp.length !== 6) {
      toast.error(language === 'te' ? 'దయచేసి చెల్లుబాటు అయ్యే 6 అంకెల OTP నమోదు చేయండి' : 'Please enter a valid 6-digit OTP');
      return;
    }

    setLoading(true);
    try {
      // Check if this is a demo OTP first
      const storedOTP = localStorage.getItem('demo_otp');
      const storedPhone = localStorage.getItem('demo_phone');
      
      if (otp === storedOTP && phoneNumber === storedPhone) {
        // Demo OTP verification
        console.log('Using demo OTP verification');
        localStorage.removeItem('demo_otp');
        localStorage.removeItem('demo_phone');
      } else {
        // Real Supabase OTP verification
        console.log('Verifying real OTP with Supabase');
        const { data, error } = await supabase.auth.verifyOtp({
          phone: `+91${phoneNumber}`,
          token: otp,
          type: 'sms'
        });

        if (error) {
          console.error('Supabase OTP verification error:', error);
          throw error;
        }
      }

      // Get or create farmer profile
      let farmerProfile = await dbHelpers.getFarmer(phoneNumber);
      
      if (!farmerProfile.data) {
        // Create new farmer profile with auto-filled data
        const newFarmer = {
          phone_number: phoneNumber,
          name: `Farmer ${phoneNumber.slice(-4)}`, // Default name
          location: 'Unknown',
          language: language
        };
        
        const { data: createdFarmer, error: createError } = await dbHelpers.createFarmer(newFarmer);
        
        if (createError) {
          throw createError;
        }
        
        farmerProfile = { data: createdFarmer[0] };
        toast.success(language === 'te' ? 'కొత్త ప్రొఫైల్ సృష్టించబడింది!' : 'New profile created!');
      }

      // Login successful with auto-filled profile
      const userData = {
        id: farmerProfile.data.id,
        phoneNumber: farmerProfile.data.phone_number,
        name: farmerProfile.data.name,
        location: farmerProfile.data.location,
        language: farmerProfile.data.language,
        loginTime: new Date().toISOString()
      };

      onLogin(userData);
      toast.success(language === 'te' ? 'లాగిన్ విజయవంతం!' : 'Login successful!');
      
    } catch (error) {
      console.error('OTP verify error:', error);
      toast.error(error.message || (language === 'te' ? 'OTP ధృవీకరణలో లోపం' : 'Failed to verify OTP. Please try again.'));
    } finally {
      setLoading(false);
    }
  };

  const handleLanguageToggle = () => {
    const newLanguage = language === 'en' ? 'te' : 'en';
    changeLanguage(newLanguage);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary-50 to-turmeric-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-md">
        {/* Language Toggle */}
        <div className="flex justify-end mb-4">
          <button 
            onClick={handleLanguageToggle}
            className="px-3 py-1 bg-gray-100 rounded-lg text-gray-700 hover:bg-gray-200 transition-colors text-sm font-semibold"
          >
            {language === 'te' ? 'English' : 'తెలుగు'}
          </button>
        </div>

        <div className="text-center mb-8">
          <div className="w-16 h-16 bg-turmeric-500 rounded-full flex items-center justify-center mx-auto mb-4">
            <span className="text-2xl">🌱</span>
          </div>
          <h1 className="text-2xl font-bold text-gray-900 mb-2">TurmerLink</h1>
          <p className="text-gray-600">{language === 'te' ? 'వ్యవసాయదారుల మార్కెట్ యాక్సెస్ ప్లాట్‌ఫారమ్' : 'Farmers Market Access Platform'}</p>
        </div>

        {step === 'phone' ? (
          <form onSubmit={handlePhoneSubmit} className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                {language === 'te' ? 'ఫోన్ నంబర్' : 'Phone Number'}
              </label>
              <div className="relative">
                <PhoneIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                <input
                  type="tel"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  placeholder={language === 'te' ? '10 అంకెల ఫోన్ నంబర్ నమోదు చేయండి' : 'Enter 10-digit phone number'}
                  className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                  maxLength="10"
                />
              </div>
            </div>

            <button
              type="submit"
              disabled={loading}
              className="w-full bg-primary-600 text-white py-3 px-4 rounded-lg font-medium hover:bg-primary-700 focus:ring-2 focus:ring-primary-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              {loading ? (language === 'te' ? 'లోడ్ అవుతోంది' : 'Loading...') : (language === 'te' ? 'OTP పంపండి' : 'Send OTP')}
            </button>
          </form>
        ) : (
          <form onSubmit={handleOtpSubmit} className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                {language === 'te' ? 'OTP నమోదు చేయండి' : 'Enter OTP'}
              </label>
              <div className="relative">
                <KeyIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                <input
                  type="text"
                  value={otp}
                  onChange={(e) => setOtp(e.target.value)}
                  placeholder={language === 'te' ? '6 అంకెల OTP నమోదు చేయండి' : 'Enter 6-digit OTP'}
                  className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                  maxLength="6"
                />
              </div>
              <p className="text-sm text-gray-500 mt-2">
                {language === 'te' ? `OTP పంపబడింది +91 ${phoneNumber}` : `OTP sent to +91 ${phoneNumber}`}
              </p>
            </div>

            <div className="flex space-x-3">
              <button
                type="button"
                onClick={() => setStep('phone')}
                className="flex-1 bg-gray-200 text-gray-800 py-3 px-4 rounded-lg font-medium hover:bg-gray-300 transition-colors"
              >
                {language === 'te' ? 'రద్దు చేయండి' : 'Cancel'}
              </button>
              <button
                type="submit"
                disabled={loading}
                className="flex-1 bg-primary-600 text-white py-3 px-4 rounded-lg font-medium hover:bg-primary-700 focus:ring-2 focus:ring-primary-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                {loading ? (language === 'te' ? 'లోడ్ అవుతోంది' : 'Loading...') : (language === 'te' ? 'ధృవీకరించండి' : 'Verify')}
              </button>
            </div>
          </form>
        )}
        
        {/* Government Login Option */}
        <div className="mt-6 pt-6 border-t border-gray-200">
          <div className="text-center">
            <p className="text-sm text-gray-600 mb-3">
              {language === 'te' ? 'ప్రభుత్వ అధికారులకు' : 'For Government Officials'}
            </p>
            <button
              onClick={onGovernmentLogin}
              className="w-full bg-blue-600 text-white py-3 px-4 rounded-lg font-medium hover:bg-blue-700 transition-colors flex items-center justify-center space-x-2"
            >
              <span>🏛️</span>
              <span>{language === 'te' ? 'ప్రభుత్వ లాగిన్' : 'Government Login'}</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
